div.setchart-nickname input {color: silver !important;}
div.setchart-nickname input:hover {background-color: #3A3A3A !important;}

ul.utilichart a:hover {background-color: #3A3A3A !important;}
ul.utilichart a:hover {border-color: black !important;}
ul.utilichart span {color: silver !important;}
span.col.statcol {color: gray !important;}
ul.utilichart a {background: none !important;}
ul.utilichart a {border-color: black !important;}
div#userbar {color: silver !important;}


button#teambuilder-setdetails.setdetails {background-color: #1C1C1C !important;}
button#teambuilder-setdetails.setdetails {box-shadow: none !important;}
button#teambuilder-setdetails.setdetails {border-color: #544E4F !important;}
button#teambuilder-setdetails.setdetails {color: silver !important;}
button#teambuilder-setdetails.setdetails:hover {background-color: #3A3A3A !important;}

button#teambuilder-setstats.setstats {background-color: #1C1C1C !important;}
button#teambuilder-setstats.setstats {box-shadow: none !important;}
button#teambuilder-setstats.setstats {border-color: #544E4F !important;}
button#teambuilder-setstats.setstats {color: silver !important;}
button#teambuilder-setstats.setstats:hover {background-color: #3A3A3A !important;}

div#teambuilder-results.teambuilder-results {background: none !important;}
div#teambuilder-results.teambuilder-results h3 {background-color: #121212 !important;}
div#teambuilder-results.teambuilder-results small {color: silver !important;}
div#teambuilder-results.teambuilder-results strong {color: silver !important;}
