button.pokemon  {color: silver !important;}
button.pokemon:hover  {background-color: #3A3A3A !important;}


div.mainsection.teambuilder button {padding-bottom: 4px !important;}
div.mainsection.teambuilder button {padding: 0px 0px !important;}

input.textbox.teamnameedit {background-color: silver !important;}
input.textbox.teamnameedit {box-shadow: none !important;}
input.textbox.teamnameedit {border-color: black !important;}

div.mainsection.teambuilder {background-color: #1C1C1C !important;}
div.mainsection.teambuilder {width: 640px !important;}

div.action-notify {background-color: #1C1C1C !important;}
