div.battle-log abbr {color: #C11B17 !important;}


div.whatdo {color: silver !important;}

div.action-waiting {background-color: #1C1C1C !important;}

ol.teamchart {background-color: #121212 !important;}

.chatbox textarea div {color: silver !important;}
ul.userlist small {color: silver !important;}
div#userbar a {color: #7653D7 !important;}

textarea.textbox {color: silver !important;}

