{
#sync, #brandLogo, #snippetContainer, #searchEngineLogo, #restorePreviousSessionSeparator



{ display: none !important; }



/* bottom icons */



.launchButton::before


{ display: none !important; }


html
{ background: #0F0D00 url("http://cdn.warcraftmovies.com/pictures/mugshots/760903_3ea.jpg") no-repeat  fixed !important; 
  background-size: cover !important; 
}


body



{ background: transparent !important; }



#contentContainer


{ background: black !important;


margin: 0 auto !important; }



#searchContainer



{ -moz-appearance: none !important; 



margin: 0 auto !important;



width: 1000px !important;



padding: 400px 30px 20px 500px !important;



border-radius: 120px !important;



border-top-color: rgba(20,245,0,0.78) !important;



border-right-color: rgba(20,245,0,0.78) !important;



border-bottom-color: rgba(20,245,0,0.78) !important;



border-left-color: rgba(20,245,0,0.78) !important;


box-shadow: -20px 40px 26px 28px rgba(0,0,0, .6) !important; }




#searchText, #searchSubmit


{ -moz-appearance: none !important; 


font-size: 17px !important;



color: rgba(5,255,0,0.78) !important;



text-shadow: 10px 20px 18px #97947F !important;



font-weight: bold !important;



font-family: Death font !important;



padding: 7px !important;



outline: 0 none !important;



border-radius: 7px !important;



border: 1px solid !important;



border-left-color: rgba(14,12,122,0.78) !important;



border-top-color: rgba(14,12,122,0.78) !important; 



border-bottom-color: rgba(14,12,122,0.78) !important;



border-right-color: rgba(14,12,122,0.78) !important;



box-shadow: 20px 20px 18px rgba(0,0,0,0.78) !important; }




#searchText, #searchSubmit



{ background: -moz-radial-gradient( rgba(22,187,209,0.78), transparent) !important; }



#searchSubmit



{ margin-left: 20px !important;



padding: 0px 5px !important;



font-weight: normal !important;



color: black !important;



text-shadow: 1px 0px 0px #000, 2px 2px 2px rgba(86,97,102, .5) !important;



font-family: Death font/* Es un estilo perzonalizado que se usa en la death note */ !important; }







#searchSubmit:hover



{ text-shadow: 1px 0px 0px #000, 1px 2px 4px #000000 !important; }


#launcher button


{ border: 10px !important;


box-shadow: inset rgba(0,0,0, 0.4) -20px 40px 20px 20px, 20px 40px 20px  #000 !important; }


#launcher, #restorePreviousSession


{ background: transparent !important;



border: none !important;



box-shadow: none !important; } 




#launcher


{ position: fixed !important;


bottom: 70% !important;



margin-left: -40px !important;



display: block !important; 



text-align: right !important; 



white-space: nowrap !important; }







.launchButton



{ background: transparent !important;



border: none !important;



font-size: 17px !important;



font-family: Death font !important;



color: rgba(48,240,0,0.78) !important;



box-shadow: inset rgba(103,24,105,0.78) -20px 60px 20px 20px, 20px 40px 20px rgba(0,0,0, .6) !important; } }
