css:
@-moz-document url-prefix(http://theoldreader.com/){
body {
	font-family: iransansz, IranSans, Tahoma, arial !important;
    font-size:13px;
    line-height:22px;
    color: rgb(14, 12, 12);
}

.list-post {
	font-family: iransansz, IranSans, Tahoma, arial !important;
    direction: rtl !important;
    text-align: right !important;
}

div .pul-left{
	font-family: iransansz, IranSans, Tahoma, arial !important;
    direction: rtl !important;
	text-align: right !important;    
}

.slider{
	padding-right: 50px;
    padding-left:30px;
}

.nav{
	font-family: BBCNassim, iransansz, IranSans, Tahoma, arial !important;
    font-weight:normal;
    font-size:14px;
	color:black;
}

.nav-list li.nav-header a {
	font-size: 20px;
	font-weight: normal;
	color:black;
}

.nav-header{
	line-height: 10px;
	color: rgb(61, 240, 17); /* range in alamat= > */
}

.nav-list .badge {
	font-size: 15px;
	font-family:"B Yekan", Yekan;
	font-weight: normal;
	text-shadow: none;
	padding: 1px 4px 1px;
	margin: 0 0 0 4px;
}

.navbar .nav li a { /* baraye oun 3 kalme IMPORT & Find Friends & User NAme */
	padding: 19px 10px 19px 10px;
	font-family: calibri, Times;
	color: #262626;
	text-shadow: none;
	font-size: 16px;
	font-weight: normal;
}

.nav-list li.nav-header a, .nav-list li.nav-header a:hover { /* liste poshe haye feed ha */
	color: rgb(0, 0, 0) !important;
	text-shadow: -1px 0px 9px rgba(12, 11, 11, 0.6);
	cursor: pointer !important;
}


h3{
	font-family: iransansz, IranSans, Tahoma, arial !important;
	font-size: 18px;
	line-height:30px;
}

.folders-list .ui-sortable{
	color: rgb(14, 12, 12);
}

.well {
     background: rgb(255, 255, 255); /* change all background to white */
     border: 1px solid #e9e9e9;
   }

.post h3 a { /* change color of post title to blue */
   color: rgb(34, 59, 241);
   background-color: rgb(250,250,250);
   }

 .post.listview .header {
   direction: rtl;
   }
 div.pull-left.span8 span {
  font-size:0px;
}

.post.listview .header .name {
float: left;
width: 240px;
}
