css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url-prefix("http://plus.googleapis.com"), 
url-prefix("https://plus.googleapis.com"), 
url-prefix("http://apis.google.com"), 
url-prefix("https://apis.google.com") 
{
.yJa,
.uPa.BJa,
.Mza.mj,
.DJa,
.RN.Nza.E5,
.xN,
.r0,
.dga
 {
  background-color: transparent !important;
  background-image: none !important;
  -moz-appearance: none !important;
}

.Kgb
 {
  background-color: rgba(255,255,255,0.2) !important;
}

.Ct,
.xN *,
.r0,
.vy, .bS,
.bmd,
.Mpa,
.dga
{
color: #999 !important;
}

/* usernames in comments */
.TD, .Ub,
.d-s,
.o-U-s,
.proflink,
.ot-anchor,
.ot-hashtag,
.qg
{
color: #ccc !important;
}

.Mga,
.Jea
{
display: none !important;
}

}
/* Youtube COSMIC PANDA */
 @-moz-document
	domain(youtube.com){
		 /* video resize */
        .player-api {
            position: relative !important; 
            left: 0px !important; 
        }
               .watch #player {
                    position: relative !important; 
                    left: 392px !important; 
        }
		#watch7-creator-bar {width:1040px!important;}	
		.watch-medium{
		    width:1080px!important;  
            position: relative!important;
            left: 0px!important;}
        
        .watch-medium .player-width {
            width: 1040px !important;
        }
        .watch-medium .player-height {
            height: 612px !important;
        }
        #yt-masthead-user #sb-button-notify {
        width: 23px !important;
        }
        #watch7-playlist-bar-next-button, #watch7-playlist-bar-prev-button { 
        width: 27px !important;
        }
                #watch7-playlist-bar-next-button span, #watch7-playlist-bar-prev-button span{ 
        padding: 8px !important;
        }
		/* guide */
@-moz-document url-prefix("http://www.youtube.com/watch") {
#guide {display: none!important}
}
.channel-header .secondary-header-contents {background-color: transparent !important;}

/** YouTube Black 2013 
/*  http://userstyles.org/styles/45732
/*  adapted from css for YouTube Earth Hour 2011, which took place March 26 2011.
/*  [http://s.ytimg.com/yt/css/earth_hour_2011-vflsXpvL9.css]
/*  last update 2013-0501-0800
/*
*/

@-moz-document 
domain("youtube.com"), 
url-prefix("http://www.youtube."), 
url-prefix("https://www.youtube.") 
{


/**********************************************************************/
/**********YOU CAN CUSTOMIZE THESE SECTIONS BUT INSTEAD I RECOMMEND YOU USE MY
/* "Overide YouTube styles" style: http://userstyles.org/styles/79953/override-youtube-styles
/* That way your customizations won't disappear when you update this style.
/* Better yet, install my "YouTube Black 2013 no options" style instead of this one -
/* then you will get automatic updates (if you have Firefox) and the customizations you've
/* made in "Overide YouTube styles" will still be in effect.
/**********************************************************************/

/*********************** background image/attachment ******************/
body 
{ 
background: url(http://i44.tinypic.com/9fsn50.jpg) #000 scroll left top !important;   
}

/********************* adjust colors and opacity here ********************/
/* adjust the parameters in rgba([red],[green],[blue],[opacity])
/* colors between 1 and 255, opacity between 0 and 1
/* opacity should be 1 if you don't want texture (or a background image)
/* use low numbers for the first three parameters to get a dark color 
/* use lower opacity for more texture or more bg image visibility
/* eg. rgba(0,0,10,.9) is dark blue, 90% opaque */

body::after /* main element - covers screen */
{} 
/*********************************************************************/

/* This affects things like pop-up menus */
/* Colors should either match the colors above or be 0 (for black) */

body .yt-uix-hovercard-card-border,
body .yt-uix-clickcard-card-border,
body #uploader-progress,
body #playlist-bar-bar-container,
body #masthead-expanded-menu-acct-sw-list,
body .gwt-PopupPanel > .popupContent  * ,
body table.gssb_c, /* auto suggest */
body table.gssb_m,
body #watch7-playlist-tray, 
body #watch7-playlist-tray-mask
   {background: rgb(0,0,0) !important;} 
/*************************************************************************/

/********************** dark sections ********************************/

/* this background color affects the central section of main pages - new method */
#contentTable::after,
body #page .branded-page-v2-primary-col::after,
body #results::after,
body #page #watch7-content #watch-discussion::after,
body #page #watch7-content #watch7-discussion::after,
body #page #watch7-content > .yt-uix-button-panel::after,
body #page #watch7-content #ytf-main-div::after,
.account-container::after,
#vm-video-list-container::after,
#creator-editor-container::after,
.vm-search-history-list::after,
#message_reading::after
{
background-color:  #1B1B1B !important;
opacity: 1 !important;
} 
/* other elements matching color - opaque */
#vm-video-actions-bar,
body .yt-uix-button.yt-uix-button-shelf-slider-pager
{
background-color: #1B1B1B !important;
} 



/************* top and bottom transitions for channel pages *********************/
#branded-page-default-bg::before{
background: -moz-linear-gradient(bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: -webkit-linear-gradient(bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: -o-linear-gradient(bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: linear-gradient(to top, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
}
#branded-page-default-bg::after {
background: -moz-linear-gradient(top, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: -webkit-linear-gradient(top, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: -o-linear-gradient(top, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
background: linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 5px, rgba(0,0,0,1) 10px, rgba(0,0,0,.3) 50%, rgba(0,0,0,0) 100%)!important;
}

/* central backgrounds for channel pages */
#branded-page-body-container #branded-page-header-container,
#branded-page-body-container .channel-layout-two-column .secondary-pane,
#branded-page-body-container #report-channel-container .yt-uix-button,
#branded-page-body-container #playlist-pane-container,
#branded-page-body-container .main-pane
{
  background-color: rgba(0,0,0,.9) !important;
  background-image: none !important;
}
#page.channel .channel-layout-two-column .primary-pane{
background-image: -moz-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,1) 300px)!important;
background: -webkit-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,1) 300px)!important;
background: -o-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,1) 300px)!important;
background: linear-gradient(to bottom, rgba(0,0,0,.9) 0%, rgba(0,0,0,1) 300px)!important;
}
.channel-layout-two-column .secondary-pane:after{
background-image: -moz-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,.8) 20%, rgba(0,0,0,.1) 80%, rgba(0,0,0,0) 100%)!important;
background: -webkit-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,.8) 20%, rgba(0,0,0,.1) 80%, rgba(0,0,0,0) 100%)!important;
background: -o-linear-gradient(top, rgba(0,0,0,.9) 0%, rgba(0,0,0,.8) 20%, rgba(0,0,0,.1) 80%, rgba(0,0,0,0) 100%)!important;
background: linear-gradient(to bottom, rgba(0,0,0,.9) 0%, rgba(0,0,0,.8) 20%, rgba(0,0,0,.1) 80%, rgba(0,0,0,0) 100%)!important;
}

/* if you want to experiment with the scrollbars this is the place */

/* default Chrome scrollbars */
#guide ::-webkit-scrollbar,
#guide ::-webkit-scrollbar-track,
#guide ::-webkit-scrollbar-corner,
#guide ::-webkit-scrollbar-thumb,
.watch-playlists-drawer ::-webkit-scrollbar,
.watch-playlists-drawer ::-webkit-scrollbar-track,
.watch-playlists-drawer ::-webkit-scrollbar-corner,
.watch-playlists-drawer ::-webkit-scrollbar-thumb,
#watch7-video-container ::-webkit-scrollbar,
#watch7-video-container ::-webkit-scrollbar-track,
#watch7-video-container ::-webkit-scrollbar-corner,
#watch7-video-container ::-webkit-scrollbar-thumb
{
border-width: 0px !important;
}
.watch-playlists-drawer ::-webkit-scrollbar-thumb
{
  background-color: #999 !important;
}


/** transparent scrollbars for Firefox
/*  http://userstyles.org/styles/
/*  update 2013-0317
*/


scrollbar, scrollcorner 
{
-moz-appearance: none !important; 
background: none !important; 
padding: 0 !important; 
border: 0 !important; 
margin: 0 !important; 
}

scrollbar:hover thumb[orient="vertical"],
scrollbar:hover thumb[orient="horizontal"],
scrollbar:hover scrollbarbutton
{ opacity: 1 !important; }


scrollbar thumb[orient="vertical"],
textbox scrollbar thumb[orient="vertical"]
{ -moz-appearance: none !important; 
opacity: .5 !important;
min-height: 30px !important;
border: none !important; 
border-radius: 0px !important; 
background-color: #444444 !important;  
background-image: -moz-linear-gradient(90deg, #444 0%, #999 5%, #444 50%, #999 95%, #444 100%) !important; 
}

scrollbar thumb[orient="horizontal"],
textbox scrollbar thumb[orient="horizontal"]
{ -moz-appearance: none !important; 
min-width: 30px !important;
opacity: .5 !important;
border: none !important;
border-radius: 0px !important;
background-color: #444444 !important;  
background-image: -moz-linear-gradient(0deg, #444444 0%, #999999 10%, #444444 50%, #999999 90%, #444444 100%) !important; } 

scrollbar[orient="vertical"]
{
min-width: 10px !important;
max-width: 10px !important;
}

html>scrollbar[orient="vertical"]
{
margin-left: -10px !important;
position: relative !important;
z-index: 99999999 !important;
}

scrollbar[orient="horizontal"]
{
max-height: 10px !important;
min-height: 10px !important; 
}
html>scrollbar[orient="horizontal"]
{
margin-top: -10px !important;
position: relative !important;
z-index: 99999999 !important;
}

scrollbarbutton
{ -moz-appearance: none !important;
border: none !important;
background: #666 !important;
opacity: .5 !important;
border-radius: 0px !important;
max-height:10px  !important;
min-height:10px  !important;
max-width:10px  !important;
min-width:10px  !important;
}

/* gives scrollbar background of Firefox theme (disabled)
tabbrowser#content
{ background: none !important; 
-moz-appearance: none !important; }

vbox#appcontent
{ background: none !important; 
-moz-appearance: none !important; }
*/

/* *********** end transparent scrollbars for Firefox ************ */



/** scrollbars for YouTube for Chrome
/*  
/*  
*/

html ::-webkit-scrollbar,
html ::-webkit-scrollbar-track,
html ::-webkit-scrollbar-corner
{
-webkit-appearance: none !important; 
background: #111 !important; 
border-width: 0px !important;
padding: 0px !important;
margin: 0px !important;
width: 10px !important;
height: 10px !important;
}

::-webkit-scrollbar-thumb:vertical,
textbox ::-webkit-scrollbar-thumb:vertical
{ -webkit-appearance: none !important; 
min-height: 30px !important;
border: none !important; 
border-radius: 0px !important; 
background-color: #333 !important;  
background-image: -webkit-linear-gradient(top, #222 0%,#444 10%,#222 50%,#444 90%,#222 100%) !important; 
border-width: 0px !important;
padding: 0px !important;
margin: 0px !important;
}
 
::-webkit-scrollbar-thumb:vertical:hover,
textbox ::-webkit-scrollbar-thumb:vertical:hover
{ 
background-color: #666 !important; 
background-image: -webkit-linear-gradient(top, #444 0%,#999 10%,#444 50%,#999 90%,#444 100%) !important; 
}
 
::-webkit-scrollbar-thumb:horizontal,
textbox ::-webkit-scrollbar-thumb:horizontal
{ -webkit-appearance: none !important; 
min-width: 30px !important;
border: none !important; 
border-radius: 0px !important;
background-color: #333 !important; 
background-image: -webkit-linear-gradient(left, #222 0%,#444 10%,#222 50%,#444 90%,#222 100%) !important; 
border-width: 0px !important;
padding: 0px !important;
margin: 0px !important;
}
::-webkit-scrollbar-thumb:horizontal:hover,
textbox ::-webkit-scrollbar-thumb:horizontal:hover
{ 
background-color: #666 !important; 
background-image: -webkit-linear-gradient(left, #444 0%,#999 10%,#444 50%,#999 90%,#444 100%) !important; 
}

::-webkit-scrollbar-button
{ -webkit-appearance: none !important; 
border: none !important;
background: #666 !important;
border-radius: 0px !important;
max-height:10px  !important;
min-height:10px  !important;
max-width:10px  !important;
min-width:10px  !important;
border-width: 0px !important;
padding: 0px !important;
margin: 0px !important;
}

/* cancel default rule */
.watch-playlists-drawer ::-webkit-scrollbar-thumb
{
  background-color: #444 !important;
}

/* *************** end Chrome scrollbars ***************** */


/************************************************************/
/** YouTube Black 2013 centering of page and player
/*  http://userstyles.org/styles/45732
/*  adapted from Youtube's new layout centered by arcadesdude
/*  [http://userstyles.org/styles/79542/]
/*  update 2013-0501
*/
/***************************************************/

/* Sets width on most items, needed for proper centering */

.site-left-aligned #page, 
.site-left-aligned #yt-masthead, 
.site-left-aligned #alerts, 
.site-left-aligned #ad_creative_1, 
.site-left-aligned #footer-hh, 
.site-left-aligned #masthead_child_div, 
#yt-masthead-container #yt-masthead, 
.exp-new-site-width #footer-hh 
{ width: 1003px !important; }

.site-left-aligned #page.my_videos_edit
{ width: 1185px !important; }

#page.channel, /* topic channel */
#masthead-expanded
{ 
width: auto !important; 
max-width: 1300px !important; 
min-width: 1003px !important; 
}
/* not signed in */
#page.home.branded-page-v2-detached-top .branded-page-v2-container,
#page.home.branded-page-v2-detached-top #content{ 
width: auto !important; 
}
#page.home.branded-page-v2-detached-top /* not signed in */
{ 
width: auto !important; 
max-width: 1150px !important; 
min-width: 1003px !important; 
}

#masthead-expanded
{ 
padding-left: 50px !important; 
}

/* set width - shrink to fit contents */
.yt-uix-pager /* pagination buttons */
{
display: table !important;
width: auto!important;
padding-left: 0px !important;
padding-right: 0px !important;
}

/* Fixes channel page backgrounds and sets width of masthead container */
.exp-new-site-width #page.branded-page, 
#yt-masthead-container
{ 
width: 100% !important;
max-width: none !important; 
}

/* This section is what does most of the centering because the width is set on these items and they are centered properly with the "margin: 0 auto !important;" css declaration. */
.site-left-aligned #page,
.site-left-aligned #yt-masthead, 
#yt-masthead-container #yt-masthead, 
#masthead-expanded,
.site-left-aligned #alerts, 
.site-left-aligned #ad_creative_1, 
.site-left-aligned #footer-hh, 
#footer-hh-container #footer-hh, 
.site-left-aligned #masthead-subnav, 
.site-left-aligned #masthead_child_div, 
.site-left-aligned #masthead-expanded-lists-container, 
.site-left-aligned #ticker .ytg-wide, 
.site-left-aligned #baseDiv, 
.exp-new-site-width #page.branded-page, 
.site-left-aligned.sidebar-expanded #page, 
.site-left-aligned.sidebar-expanded #yt-masthead, 
.site-left-aligned.sidebar-expanded #alerts, 
.site-left-aligned.sidebar-expanded #ad_creative_1, 
.site-left-aligned.sidebar-expanded #footer-hh, 
.site-left-aligned.sidebar-expanded #masthead-subnav, 
.site-left-aligned.sidebar-expanded #masthead_child_div, 
.site-left-aligned.sidebar-expanded #masthead-expanded-lists-container, 
.site-left-aligned.sidebar-expanded #ticker .ytg-wide, 
.site-left-aligned.sidebar-expanded #baseDiv,
.yt-uix-pager,
body.site-left-aligned #watch7-creator-bar
{ 
margin-left: auto !important; 
margin-right: auto !important; 
}

/*  center footers */
body.site-left-aligned:not(#foo) #footer-hh,
body.site-left-aligned:not(#foo) #footer-hh-main,
body.site-left-aligned:not(#foo) #footer-hh-links,
body.site-left-aligned:not(#foo) #footer-hh-links-primary,
body.site-left-aligned:not(#foo) #footer-hh-links-secondary,
body.site-left-aligned:not(#foo) #footer,
body.site-left-aligned:not(#foo) #footer-main,
body.site-left-aligned:not(#foo) #footer-links,
body.site-left-aligned:not(#foo) #footer-links-primary,
body.site-left-aligned:not(#foo) #footer-links-secondary
{
display: table !important;
margin-left: auto !important;
margin-right: auto !important;
width: auto!important;
padding-left: 0px !important;
padding-right: 0px !important;
}

/* Fixes search results column to be the full width (rest of the area that would have been occupied by the recommended videos column on the right side). */
#page.search-base .primary-col { width: auto !important; }
#results { width: 783px !important; }
#page.search-base #content  .yt-alert 
{margin-left: -180px !important;  /* allow into guide space */
margin-top: 20px !important;}

/************* watch page ***********************/

/* leave #page.watch  full width */
#page.watch { width: 100% !important; }

/* allow fixed position element to be centered w auto margin */
#watch7-playlist-container.playlist-floating.scrolled #watch7-playlist-scrollfloater{ 
left: 0px !important; 
right: 0px !important; 
}

/* reserve space for guide */
body.guide-expanded #watch7-main-container,
body.guide-expanded #player,
body.guide-expanded #watch7-playlist-container {
margin-left: 225px !important;
margin-right: 225px !important;
padding-left: 0px !important;
padding-right: 0px !important;
overflow: visible !important; 
}
/* guide collapsed - decrease main padding */
html body.guide-collapsed #player,
body.guide-collapsed #page.watch #watch7-main-container,
html body.guide-collapsed #watch7-playlist-container,
body.guide-collapsed.ytwp-window-player #page.watch #watch7-playlist-container /* Resize YouTube Player to Window Size script */
{
margin-left: 40px !important;
margin-right: 40px !important;
padding-left: 0px !important;
padding-right: 0px !important;
overflow: visible !important; 
}
/* guide collapsed or YTC lowered guide - remove video padding */
html body.guide-collapsed .watch-medium #player,
/*html body.guide-collapsed #watch7-container.watch-playlist #player,*/
html body.guide-collapsed .watch-medium #watch7-playlist-container,
#page.watch #watch7-container.ytcenter-custom-resize #player,
#page.watch #watch7-container.ytcenter-custom-resize #watch7-playlist-container,
#page.watch #watch7-container, /* 13-03-20 */
html body.ytwp-window-player #player /* Resize YouTube Player to Window Size script */
{
margin-left: 0px !important;
margin-right: 0px !important;
padding-left: 0px !important;
padding-right: 0px !important;
overflow: visible !important; 
}

/* smaller display - move guide left and decrease padding */
@media screen and (max-width: 1350px) {
#page.watch #guide-container { left: 10px !important; }

body.guide-expanded #watch7-main-container,
body.guide-expanded #player,
body.guide-expanded #watch7-playlist-container 
{
margin-left: 195px !important;
margin-right: 195px !important;
padding-left: 0px !important;
padding-right: 0px !important;
overflow: visible !important; 
}

}

/* prevent overlap of collapsed guide labels with main content */
@media screen and (max-width: 1270px) {
.guide-collapsed .guide-module-toggle-label { visibility: hidden !important; }
}

body #page.watch #watch7-main,
body #page.watch #watch7-container.watch-medium .watch7-playlist,
body #page.watch #watch7-container:not(.watch-medium)
{ 
display: table !important; 
width: auto !important; 
}


body #page.watch #watch7-container:not(.watch-medium) .watch7-playlist,
body.ytwp-window-player #page.watch .watch7-playlist
{ 
min-width: 1045px !important; 
width: 1045px !important; 
}

.watch7-playlist-bar-right{
width: 405px !important;
}


body #page.watch #watch7-main,
body #page.watch .watch7-playlist-bar,
body #page.watch .watch-medium #player-api,
body #page.watch .watch-medium .watch7-playlist,
body #page.watch #watch7-container:not(.watch-medium)
{ 
margin-left: auto !important; 
margin-right: auto !important; 
}

body.guide-enabled .watch7-playlist
{ 
padding-left: 0px !important;
padding-right: 0px !important;
}



/*********************** end of centering ********************/

/*[[sidebar]]*/

/************* show channel backgrounds (placeholder *************/

/***************** end options ****************************/

/* create element for main background color - fills screen */
body::after {
content: ''!important;
position: fixed !important;
top: 0px!important;
left: 0px!important;
right: 0px!important;
bottom: 0px!important;
z-index:  -1 !important;
}

/* create elements for dark sections */
#contentTable, /* analytics */
body #page .branded-page-v2-primary-col,  /* home page */
body #results, /* search results */
body #page #watch7-content #watch-discussion, /* comments */
body #page #watch7-content #watch7-discussion, /* comments */
body #page #watch7-content > .yt-uix-button-panel,
body #page #watch7-content #ytf-main-div, /* yousable tubefix */
.account-container, /* settings */
#vm-video-list-container, /* video manager */
#creator-editor-container, /* edit */
.vm-search-history-list,
#message_reading /* inbox */
{
position: relative !important;
z-index:  1 !important;
} 
#contentTable::after,
body #page .branded-page-v2-primary-col::after,
body #results::after,
body #page #watch7-content #watch-discussion::after,
body #page #watch7-content #watch7-discussion::after,
body #page #watch7-content > .yt-uix-button-panel::after,
body #page #watch7-content #ytf-main-div::after,
.account-container::after,
#vm-video-list-container::after,
#creator-editor-container::after,
.vm-search-history-list::after,
#message_reading::after
{
content: ''!important;
position: absolute !important;
visibility: visible !important;
height: auto !important;
width: auto !important;
