/* Expand description button - create horizontal rule */

#watch-description .yt-uix-expander-head .yt-uix-button::before {
content: ''!important;
position: absolute!important;
top: 7px!important;
left: -183px!important;
width: 180px !important;
border-width: 1px !important;
border-style: solid !important;
border-color: rgba(255,255,255,.2) !important;
}
#watch-description .yt-uix-expander-head .yt-uix-button::after {
content: ''!important;
position: absolute!important;
top: 7px!important;
left: 83px!important;
width: 180px !important;
border-width: 1px !important;
border-style: solid !important;
border-color: rgba(255,255,255,.2) !important;
}

#watch-description .yt-uix-expander-head .yt-uix-button{
width: 84px !important;
}

/* triangles */
.yt-subscription-button-subscriber-count-unbranded-vertical:after,
