/******************* transitions *************************/

#masthead-expanded
{
  display: block !important;
  height: 159px !important;
  overflow: hidden !important;
  -moz-transition: all .6s ease-out !important;
  -webkit-transition: all .6s ease-out !important;
  -o-transition: all .6s ease-out !important;
}
#masthead-expanded.hid
{
  height: 0px !important;
}


#masthead-expanded-container
{
  display: block !important;
  position: relative !important;
  top: 0px !important;
  -moz-transition: all .6s ease-out !important;
  -webkit-transition: all .6s ease-out !important;
  -o-transition: all .6s ease-out !important;
}
#masthead-expanded.hid #masthead-expanded-container
{
  top: -159px !important;
}

}
