css:
@-moz-document url-prefix("http://craftsite.pl/forum/") {
body {
background-image: url("http://i.imgur.com/B1ZrHAz.png"), url("http://i.imgur.com/C94UpyQ.jpg"), url("http://i.imgur.com/J4CYRNR.jpg") !important;
background-repeat: repeat, no-repeat, repeat !important;
background-position: center top, center top, center top !important;
}
#primary_nav {
background: 0;
background-color: rgba(255, 255, 255, 0) !important;
padding: 4px 0 0 0;
margin-top: 224px;
}
#primary_nav #quickNavLaunch {
padding: 15.5px 18px 15.5px;
}
#ajax_loading {
background: rgb(201, 201, 201);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(122, 122, 122)), color-stop(100%,rgb(94, 94, 94)));
border: none;
}
#branding {
background: 0 !important;
background-color: rgba(255, 255, 255, 0) !important;
border-color: rgba(255, 255, 255, 0) !important;
}
td {
padding: 0px 0 0 0;
padding-top: 0px;
}
.main_width {
padding-bottom: 0 !important;
padding-top: 5px !important;
}
#content {
background: #fff !important;
border-radius: 10px 10px 10px 10px !important;
}
img[src="http://craftsite.pl/forum/public/style_images/1_logo.png"] {
visibility: hidden !important;
}
#header_bar {
text-align: right;
position: fixed;
z-index: 9999;
width: 100%;
}
#search {
margin: 50px 0;
}
#community_app_menu>li>a {
background-image: url("http://i.imgur.com/HDaqGLz.png"), url("http://i.imgur.com/C4u2MeJ.png"), url("http://i.imgur.com/MoHMFVk.png");
background-position: top left, top right, top center;
background-repeat: no-repeat, no-repeat, repeat;
padding: 15.5px 40px 15.5px;
text-shadow: 2px 2px 3px rgb(0, 0, 0) !important;
color: rgb(255, 255, 255);
}
#community_app_menu>li>a:hover, #community_app_menu>li>a.menu_active {
background-image: url("http://i.imgur.com/3PTMakd.png"), url("http://i.imgur.com/GMcEOdD.png"), url("http://i.imgur.com/ZzXPtfA.png");
background-position: top left, top right, top center;
background-repeat: no-repeat, no-repeat, repeat;
text-shadow: 2px 2px 3px rgb(0, 0, 0) !important;
}
#community_app_menu>li.active>a {
background-image: url("http://i.imgur.com/Uh6w5M5.png"), url("http://i.imgur.com/nqddfxx.png"), url("http://i.imgur.com/PmBZvx3.png");
background-position: top left, top right, top center;
background-repeat: no-repeat, no-repeat, repeat;
color: rgb(252, 252, 252);
font-weight: normal;
text-shadow: 2px 2px 3px rgb(0, 0, 0) !important;
}
#search_wrap {
background: rgba(71, 71, 71, 0.25) !important;
padding: 0 26px 0 4px;
height: 26px;
line-height: 25px;
border-radius: 3px 4px 4px 3px;
}
#search_options {
color: rgb(148, 113, 75);
height: 20px;
line-height: 20px;
margin: 3px 3px 3px 0;
padding: 0 6px;
border-radius: 3px;
background: transparent;
}
#main_search {
color: #ccc;
}
#search .submit_input {
background: rgba(104, 104, 104, 0.44) url(http://craftsite.pl/forum/public/style_images/master/search_icon.png) no-repeat 50%;
border: none;
border-radius: 12px 12px 12px 12px;
}
#copyright {
text-shadow: 0px 0px 0px #fff !important;
}
.statistics {
border-top: 0px solid #d8d8d8 !important;
}

.post_block h3 {
background: rgb(211, 211, 211) !important;
}
.general_box h3 {
background: rgb(230, 230, 230) !important;
color: rgb(34, 34, 34) !important;
border-radius: 10px 10px 0 0;
}
.ipsVerticalTabbed_tabs li a:hover {
background: rgb(228, 228, 228) !important;
}
.ipsBox {
background: rgb(236, 236, 236) !important;
}
.unread .altrow, .unread.altrow {
background-color: rgb(236, 236, 236) !important;
}
.ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {
background: rgb(85, 85, 85) !important;
}
.ipsSideMenu ul li.active a {
background: rgb(158, 158, 158) url(http://craftsite.pl/forum/public/style_images/master/icon_check_white.png) no-repeat 6px 8px;
}
.input_submit.alt {
background: rgb(230, 230, 230) !important;
border-color: rgb(231, 231, 231) !important;
}
body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
background-color: rgb(207, 207, 207) !important;
}
.pagination .pages li.active {
background: rgb(151, 151, 151) !important;
}
#shouts-global-resizer, #shouts-resizer {
background-color: rgb(238, 238, 238) !important;
}
#footer_utilities {
border-radius: 0 0 10px 10px;
background: transparent !important;
}
#user_latest_status > div {
border-radius: 10px;
}
.status_update {
background: rgb(211, 211, 211);
}
img[src="http://craftsite.pl/forum/public/style_images/master/f_icon.png"] {
width: 0px !important;
height: 50px;
padding-right: 50px;
background-image: url("http://i.imgur.com/cobWwxV.png") !important;
}
img[src="http://craftsite.pl/forum/public/style_images/master/f_icon_read.png"] {
width: 0;
height: 50px;
padding-right: 50px;
background-image: url("http://i.imgur.com/vk2hjaf.png");
}
img[src="http://craftsite.pl/forum/public/style_images/master/f_redirect.png"] {
width: 0px;
height: 50px;
padding-right: 50px;
background-image: url("http://i.imgur.com/vk2hjaf.png");
}
img[src="http://craftsite.pl/forum/public/style_images/master/t_unread.png"] {
width: 0;
height: 15px;
padding-right: 17px;
background-image: url("http://i.imgur.com/hjMCkvY.png");
}
.maintitle {
background-image: url("http://i.imgur.com/lpMp7pB.png"), url("http://i.imgur.com/N2VYnxB.png") !important;
background-repeat: repeat-x, repeat !important;
background-position: center -27%, top center !important;
border-color: rgb(184, 184, 184) !important;
border-radius: 7px 7px 0 0;
}
.maintitle a {
color: #fff;
text-shadow: 2px 2px 3px rgb(0, 0, 0) !important;
}
.ipsSideBlock h3 {
background-color: rgb(213, 213, 213) !important;
border-radius: 7px 7px 0 0;
  padding: 7px 10px;
}
.header {
background: transparent;
}
.pagination .back a:hover, .pagination .forward a:hover {
background-color: rgb(139, 130, 135) !important;
}
}
