css:
@-moz-document domain("reddit.com") {

@-webkit-keyframes scroll{0%{top:100%}100%{top:-100%}}
@-moz-keyframes scroll{0%{top:100%}100%{top:-100%}}
@-ms-keyframes scroll{0%{top:100%}100%{top:-100%}}
@-o-keyframes scroll{0%{top:100%}100%{top:-100%}}
@keyframes scroll{0%{top:100%}100%{top:-100%}}


a[href*='#starwars']{
	display:block;
	width:100%;
	height:100%;
	text-decoration:none !important;
	background-color:#000
}
a[href*='#starwars'] strong{
	position:relative;
	display:block;
	margin-top:-620px;
	width:100%;
	height:1000px;
	font-size:200%;
	text-align:justify;
	vertical-align:top;
	overflow:hidden;
	-webkit-transform-origin:50% 100%;
	-moz-transform-origin:50% 100%;
	-ms-transform-origin:50% 100%;
	-o-transform-origin:50% 100%;
	transform-origin:50% 100%;
	-webkit-transform:perspective(300px) rotateX(25deg);
	-moz-transform:perspective(300px) rotateX(25deg);
	-ms-transform:perspective(300px) rotateX(25deg);
	-o-transform:perspective(300px) rotateX(25deg);
	transform:perspective(300px) rotateX(25deg)
}
a[href*='#starwars'] strong:after{
	position:absolute;
	content:' ';
	top:0;
	left:0;
	width:100%;
	height:100%;
	background-image:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#000),color-stop(100%,transparent));
	background-image:-webkit-linear-gradient(top,#000 0,transparent 100%);
	background-image:-moz-linear-gradient(top,#000 0,transparent 100%);
	background-image:-ms-linear-gradient(top,#000 0,transparent 100%);
	background-image:-o-linear-gradient(top,#000 0,transparent 100%);
	background-image:linear-gradient(top,#000 0,transparent 100%);
	pointer-events:none
}
a[href*='#starwars'] strong em{
	position:relative;
	display:block;
	font-family:"Droid Sans",arial,verdana,sans-serif;
	font-style:normal !important;
	font-weight:700 !important;
	color:#ff6 !important;
	top:100%;
	-webkit-animation:scroll 75s linear 4s infinite;
	-moz-animation:scroll 75s linear 4s infinite;
	animation:scroll 75s linear 4s infinite
}

}
