css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plus.google.com") {
    /* remove scrollbar from comments */
    .Dx { max-height: 99999px !important; }
    
    /* remove trash from notifications */
    .eFa, .CQb  { display: none !important; } 
    /*.aac { max-height: 50px !important; }*/
    .MNn0h { margin: 0 !important; width: 100% !important; }
    .pH4Gee.HEAp2c { min-height: 0px !important; padding-bottom: 0px !important; width: 100% !important; }
    .A4bLHc.z6vYse.ez0Ipb { display: none !important; }
    
    /* make notifications wider */
    #gbsf, #gbsfw, #gbsfw > iframe, .KPc { width: 520px !important; }
    .hga, .tta, .Wwb, .zta, .gb_ha { margin: 0 !important; width: 100% !important; }
    .Jub, .Hub, .Gub { width: 380px !important; }
    .A4bLHc.z6vYse { width: 480px !important; margin-left: 0 !important; } /* everything else */
    .zja > .Bc > .ev > .rG { width: 480px !important; max-width: 480px !important; } /*sharebox*/
    .JWa.Kac { width: 460px !important; } /* tiny opaque line */

    /* remove button */
    /*[[button]]*/
   
    /* center images and video in notifications */
    .yaax { text-align: center !important; }
    .Zo, .Ai { width: auto !important; }

    /* set background color in comments to white */
    .Cx, .WR, .vq, .vy, .TR, .Fr, .y1 { background-color: white !important; }
    
    /* set font color to black */
    .editable, .Ct, .tv { color: black !important; }
    
    /* set font face to Arial */
    /*[[font]]*/
    
    /* remove boldness */
    a.proflink, a.ot-hashtag { font-weight: normal !important; }
    
    /* remove blue font from names */
    .qm, span.proflinkPrefix { color: black !important; }
    
    /* paint necessary links blue */
    a.ot-hashtag, a.proflink, .Ct a.ot-anchor { color: rgb(66, 127, 237) !important; }
    
    /* fix post pages */
    .IIb { width: 520px !important; }
    .Gyd { display: none !important; }
    .lNd { top: 0px !important; margin: 0 auto !important; }
    
    /* fix image height */
    /*[[images]]*/
    
    /* compact muted posts */
    .QY { min-height: 0 !important; height: 100px !important; padding-top: 10px !important; }
    .a3, .f3 { display: none !important; }
    .vQ { padding-top: 10px !important; margin-top: 0 !important; }
    
    /* remove suggested users, invites to communities, etc. */
    div[data-iid="sii2:116"],
    div[data-iid="sii2:115"],
    div[data-iid="sii2:112"] { display: none !important; }
}
