@-moz-document domain("plus.google.com") {
/* ================ */
/*  ヘッダ部の変更  */
/* ================ */
/* ヘッダ全体 */
div.ORa > div:first-child > div:first-child > div:first-child {
	border-top	: none !important;
	background	: -moz-linear-gradient(top, #FFF, #09E) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#09E)) !important;
}
/* 検索ボックス */
input#searchBox, div#gbqfqw {
	border			: none !important;
	border-radius		: 20px !important;
	background		: -moz-linear-gradient(top, #6CF, #FFF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#6CF), to(#FFF)) !important;
	margin-right		: 10px !important;
}


/* ============== */
/*  背景色を変更  */
/* ============== */
/* 基本 */
div#contentPane {
	background-color	: #EFF !important;
}
/* 投稿本文の裏側（アクティビティ切替時に見える） */
div[id^='update-'] {
	background	: #EFF !important;
}
/* プロフィールページ／プロフィール － 投稿 の投稿カード間 */
div.K9UU0, div.ee3yFe {
	background-color	: #EFF !important;
}


/* ============== */
/*  共有ボックス  */
/* ============== */
div[guidedhelpid='sharebox_textarea'] {
	color	: #CCC !important;
}


/* ================ */
/*  投稿の共通項目  */
/* ================ */
/* 投稿全体のボーダーライン、上0px下2pxになってるのを修正 */
div[id^='update-'] > div {
	border-bottom-width	: 1px !important;
	border-top-width	: 1px !important;
}
/* 投稿全体のボーダーライン、上0px下2pxになってるのを修正 */
div[id^='update-'] > div > div.r6Rtbe {
	border-bottom-width	: 1px !important;
	border-top-width	: 1px !important;
}
/* 投稿者の名前を大きく表示 */
div[id^='update-'] > div > div.ii > div > div > header > h3 {
	font-size	: x-large !important;
}
/* 「コメントを追加」ボックス */
div[id^='update-'] div.bI {
	border-radius		: 0px 0px 7px 7px !important;
	margin-right		: 0px !important;
	background-color	: #FFF !important;
}
/* アクティビティ上部の背景色 */
div[id^='update-'] > div > div.oFrhDf > div:not(:last-child) {
	background	: #DDD !important;
}
/* アクティビティ上下の境界線 */
div[id^='update-'] > div > div.oFrhDf > div:last-child {
	border-top	: 1px solid #BBB !important;
}
/* アクティビティ下部の背景色 */
div[id^='update-'] > div > div.oFrhDf {
	background	: -moz-linear-gradient(top, #DDD, #EEE) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#DDD), to(#EEE)) !important;
}
/* お知らせカードの見出し */
div#contentPane div.JH {
	background	: -moz-linear-gradient(top, #BEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#BEF), to(#FFF)) !important;
}
/* お知らせカードの上下 */
div#contentPane div.Yy {
	padding-bottom	: 0px !important;
	padding-top		: 0px !important;
}


/* ============ */
/*  通常の投稿  */
/* ============ */
/* 投稿本文の背景色 */
div[id^='update-'] > div > div.ii:first-child {
	background	: #CEF !important;
}
/* +1, 共有, Hangoutボタン（通常時） */
div[id^='update-'] > div > div.ii:first-child > div.LI > div[role='button'][id^='po-'][aria-pressed='false'],
div[id^='update-'] > div > div.ii:first-child > div.LI > div[role='button'].dk {
	border-color		: #CEF !important;
	background			: transparent !important;
	-webkit-box-shadow	: none !important;
	box-shadow			: none !important;
}
/* +1, 共有, Hangoutボタン（投稿ホバー時） */
div[id^='update-']:hover > div > div.ii:first-child > div.LI > div[role='button'][id^='po-'][aria-pressed='false'],
div[id^='update-']:hover > div > div.ii:first-child > div.LI > div[role='button'].dk {
	border-color	: #BDF !important;
	background		: -moz-linear-gradient(top, #EFF, #BDF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#EFF), to(#BDF)) !important;
}
/* +1, 共有, Hangoutボタン（ボタンホバー時） */
div[id^='update-'] > div > div.ii:first-child > div.LI > div[role='button'][id^='po-'][aria-pressed='false']:hover,
div[id^='update-'] > div > div.ii:first-child > div.LI > div[role='button'].dk:hover {
	border-color	: #ADF !important;
	background		: -moz-linear-gradient(top, #FFF, #ADF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#ADF)) !important;
}
/* 本文下部の境界線（コメント欄がない状態でも表示されているため非表示に） */
div[id^='update-'] > div > div.ii:first-child > div.LI {
	border-bottom	: 0px !important;
}
/* 本文とコメント欄の境界線（コメント欄上部に設定する） */
div[id^='update-'] > div > div.ii:first-child > div > div.FE > div:first-child {
	border-top	: 1px solid #ACD !important;
}
/* コメント欄の背景色 */
div[id^='update-'] > div > div.ii:first-child > div > div.FE {
	background	: -moz-linear-gradient(top, #CEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#CEF), to(#FFF)) !important;
}
/* 「n件のコメント」部分の背景色 */
div[id^='update-'] > div > div.ii:first-child > div > div.FE > div > div.tr,
div[id^='update-'] > div > div.ii:first-child > div > div.FE > div > div.tr > div.iP,
div[id^='update-'] > div > div.ii:first-child > div > div.FE > div > div.tr > div.iP > span[role='button'].sF,
div[id^='update-'] > div > div.ii:first-child > div > div.FE > div > div.tr > div.iP > div.Su {
	background-color	: transparent !important;
}
/* 投稿本文のオプションメニューボタン背景色（ホバー時に出る▽） */
div[id^='update-'] > div > div.ii:first-child > div > span[role='button'] {
	background-color	: transparent !important;
}


/* ============ */
/*  注目の投稿  */
/* ============ */
/* 「注目＆おすすめの投稿」 */
div[id^='update-'] > div > div.ZX:first-child {
	background	: #FEE !important;
}
/* 投稿本文の背景色 */
div[id^='update-'] > div > div.ii:not(:first-child) {
	background	: #FEE !important;
}
/* +1, 共有, Hangoutボタン（通常時） */
div[id^='update-'] > div > div.ii:not(:first-child) > div.LI > div[role='button'][id^='po-'][aria-pressed='false'],
div[id^='update-'] > div > div.ii:not(:first-child) > div.LI > div[role='button'].dk {
	border-color		: #FEE !important;
	background			: transparent !important;
	-webkit-box-shadow	: none !important;
	box-shadow			: none !important;
}
/* +1, 共有, Hangoutボタン（投稿ホバー時） */
div[id^='update-']:hover > div > div.ii:not(:first-child) > div.LI > div[role='button'][id^='po-'][aria-pressed='false'],
div[id^='update-']:hover > div > div.ii:not(:first-child) > div.LI > div[role='button'].dk {
	border-color	: #FDD !important;
	background		: -moz-linear-gradient(top, #FFE, #FDD) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFE), to(#FDD)) !important;
}
/* +1, 共有, Hangoutボタン（ボタンホバー時） */
div[id^='update-'] > div > div.ii:not(:first-child) > div.LI > div[role='button'][id^='po-'][aria-pressed='false']:hover,
div[id^='update-'] > div > div.ii:not(:first-child) > div.LI > div[role='button'].dk:hover {
	border-color	: #FDC !important;
	background		: -moz-linear-gradient(top, #FFF, #FDC) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#FDC)) !important;
}
/* 本文下部の境界線（コメント欄がない状態でも表示されているため非表示に） */
div[id^='update-'] > div > div.ii:not(:first-child) > div.LI {
	border-bottom	: 0px !important;
}
/* 本文とコメント欄の境界線（コメント欄上部に設定する） */
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE > div:first-child {
	border-top	: 1px solid #DCC !important;
}
/* コメント欄の背景色 */
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE {
	background	: -moz-linear-gradient(top, #FEE, #FFF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FEE), to(#FFF)) !important;
}
/* 「n件のコメント」部分の背景色 */
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE > div > div.tr,
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE > div > div.tr > div.iP,
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE > div > div.tr > div.iP > span[role='button'].sF,
div[id^='update-'] > div > div.ii:not(:first-child) > div > div.FE > div > div.tr > div.iP > div.Su {
	background-color	: transparent !important;
}
/* 投稿本文のオプションメニューボタン背景色（ホバー時に出る▽） */
div[id^='update-'] > div > div.ii:not(:first-child) > div > span[role='button'] {
	background-color	: transparent !important;
}


/* ============== */
/*  細々したもの  */
/* ============== */
/* メンション */
span.proflinkWrapper {
	background				: #EEE !important;
	border					: 1px solid #DDD !important;
	border-radius			: 3px !important;
	-webkit-border-radius	: 3px !important;
	display					: inline-block !important;
	margin					: 0 1px !important;
	padding					: 0 1px !important;
	vertical-align			: baseline !important;
	white-space				: nowrap !important;
}
/* メンションのリンク前の"+" */
span.proflinkWrapper > span.proflinkPrefix {
	color	: #888 !important;
}
/* メンションのリンク部分 */
span.proflinkWrapper > a.proflink {
	text-decoration	: none !important;
	font			: normal 13px/1.4 Roboto,arial,sans-serif !important;
}
/* 一般公開／友だちの友だちサークル　ボタンを赤に */
span.i-j-h-ma-xi {
	background	: #E74B37 !important;
	border		: 1px solid #E74B37 !important;
}
/* 「共有」「コメントを投稿」などのボタンを青に */
div[role="button"].c-b-da {
	background	: #427FED !important;
	border		: 1px solid #427FED !important;
}
/* スクロールバー */
::-webkit-scrollbar-thumb {
	background-color : rgba(102, 204, 255, .3) !important;
}
::-webkit-scrollbar-thumb:hover {
	background-color : rgba(102, 204, 255, .7) !important;
}


/* ============ */
/*  特殊カード  */
/* ============ */
/* 今後のイベント */
div[data-iid='sii2:113'] > div,
div[data-iid='sii2:113'] > div > div:first-child > * {
	background	: #FFD !important;
}
}
