@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain(strims.pl) {
#top_bar div.top_bar_submenu ul li { display: block!important;float:left!important;width:150px!important;padding:2px 0 2px 0!important;margin:2px 2px 2px 2px!important;border:1px solid #1c4a72!important;background:#336796!important;}
#top_bar_wrapper div.top_bar_submenu {margin-top:-1px!important;}
#top_bar_wrapper > ul > li.section_s_Glowny div.top_bar_submenu {width:790px!important;margin-top:-1px!important;}
#top_bar_wrapper > ul > li.section_s_Subskrybowane div.top_bar_submenu {width:800px!important;margin-top:-1px!important;}
#top_bar_wrapper > ul > li.section_s_Moderowane div.top_bar_submenu {width:790px!important;margin-top:-1px!important;}
#top_bar_wrapper > ul > li.section_u_Subskrybowani div.top_bar_submenu {width:800px!important;margin-top:-1px!important;}
#top_bar_wrapper > ul > li.section_d_Subskrybowane div.top_bar_submenu {width:315px!important;margin-top:-1px!important;}

form .form_row .input_text, form .form_row .input_textarea {width:95%;}
#top_bar_wrapper > ul > li.section_s_Glowny a  {background:#0e2840!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;border-top:1px solid #40c300!important;}
#top_bar_wrapper > ul > li.section_s_Glowny a:hover {background:#4179ab!important;}
#top_bar_wrapper > ul > li.section_s_Subskrybowane  a {background:#1c4162!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;border-top:1px solid #49de00!important;}
#top_bar_wrapper > ul > li.section_s_Subskrybowane a:hover {background:#4179ab!important;}
#top_bar_wrapper > ul > li.section_s_Moderowane a  {background:#29547c!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;border-top:1px solid #54ff00!important;}
#top_bar_wrapper > ul > li.section_s_Moderowane a:hover {background:#4179ab!important;}
#top_bar_wrapper > ul > li.section_u_Subskrybowani a  {background:#315f8a!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;border-top:1px solid #68ff1e!important;margin-right:1px!important;}

#top_bar_wrapper>ul>li>a{background:#315f8a!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;border-top:1px solid #68ff1e!important;margin-right:1px!important;}
#top_bar_more{background:#123455!important;padding:0 30px 0 30px!important;font-weight:bold!important;color:#fff!important;margin-right:1px!important;}

#top_bar_wrapper > ul > li.separator {background:#29547c!important;color:#29547c!important;margin-left:1px!important;margin-right:1px!important;}
#top_bar_wrapper > ul > li.section_u_Subskrybowani  a:hover {background:#4179ab!important;}

#top_bar div.top_bar_submenu ul li a {background:#336796!important;padding:0!important;font-weight:normal!important; border:none!important;}

#search_right_box #search_right_box_text_wrapper:before{ content:'';}
#top_bar_wrapper > ul > li.separator {display:none;}

