css:
@-moz-document domain("scratch.mit.edu") {
*{background-color:transparent}
a{color:#00C}
a:hover{color:#009}
#pagewrapper{background-color:#222}
#footer{background-color:#004}
#footer p{color:#CCC}
#footer-menu a{color:#505 !important}
#footer-menu a:hover{color:#303 !important}
.box-content,.box-content :not(.cmouth):not(.hat):not(.stack){background-color:#BBB !important}
input, textarea{border-color:#FFF !important;background-color: rgba(234, 234, 238, 0.6);}
#footer select{background-color:#CCC;color:#FFF}
a{text-decoration:none !important}
.title{font-weight:bold !important}
#topnav{background-color:#00A}
.closed-topic a,.closed-topic a:hover{color:#AAA !important}
.pagination,.linkst li{color:#BBB}
.popover-content{background-color:#00A !important}
blockquote{border-radius:3px;border:1px solid #20D !important}
.code{border-radius:3px;border:1px solid #BBC !important}
.button *{border:none;background-color:transparent !important}
.search input{background-color:#EEE}
}
