css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/*Swan Queen marriage Tumblr dashboard theme
background courtesy of Marynesq.tumblr.com, used with permission
theme created by pikacat-ships-rizzles-sq.tumblr.com
Version 1.0*/
div.l-header {

background:#1E1E1E !important;

border-radius: 15px !important;

border: 1px outset #444;

margin-top: -8px !important;

margin-bottom: 0px !important;

padding-bottom: 10px !important;

box-shadow: 0px 10px 8px -12px #000 !important;

height: 60px !important;}

.selection_nipple.white,
.selection_nipple {
  border: 0px !important;
background-image:none !important;
  width:  24px !important;
  height: 24px !important;
  margin-top: -12px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
}

div.l-content #right_column li {
background-color: rgba(200,200,200, .5) !important;
opacity: .8 !important;
}

div.l-content #right_column {
background-color: transparent !important;
}

div.l-content #right_column.swipeable {
background: #000 !important;
}
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, 

#dashboard_members{background: url(http://i1301.photobucket.com/albums/ag106/theVillageIdiot3/SQmarriagebg_zpsab3a80e0.jpg) 25% 50% no-repeat fixed !important;}

/*--------------------image not mine, used with permission from marynesq----------------------------*/

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0) !important}

#right_column .controls_section li a {

   color: #000 !important;

}

#left_column #posts .notification .avatar {opacity:0.7 !important;}

#left_column #posts .notification .avatar:hover{opacity:1 !important;}

#right_column{background-color: #FF0000 opacity:0 !important;}

#new_post_notice_container{color:#fff!important}

#posts .notification {

background: rgba(200,200,200, .5) !important;

border: 1px rgba(200,200,200, .9) solid !important;

margin: -1px;

}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {

border-right: 7px solid #000000 !important;

}

#posts .notification .nipple, #posts .notification.alt .nipple {

border-right: 6px solid #163056 !important;

  }
}
