style_setting_options.value:
.content-background { background-color: #EEF2FF!important; color: #000000!important; font-size: 12pt!important; }
.content-background a, .spoiler:hover a { color: #34345C!important; }
.content-background a:hover, .reflink a:hover, .spoiler:hover a:hover, .postmessage a  { color: #DD0000!important; }
.adminbar { font-size: inherit!important; } .adminbar a { font-weight: normal!important; }
.logo { color: #AF0A0F!important; font-weight: normal!important; text-align: center!important; }
.replymode { background-color: #0010E0!important; margin-bottom: 0!important; } .catalogmode { background-color: #0040E0!important; }
.replymode, .catalogmode { border: none!important; color: #FFFFFF!important; font-weight: normal!important; border-radius: 0!important; }
.postblock { background-color: #9988EE!important; color: inherit!important; padding: 0 5px; }
.postblock[style="padding-top:14px"] { vertical-align: middle!important; padding-top: 0!important; }
.rules { font-size: 12px!important; } .rules ul { list-style-position: inside; margin: 0!important; padding: 0!important; }
.filesize { color: inherit!important; font-size: inherit!important; }
.filetitle, .replytitle, .oldpost { color: #0F0C5D!important; }
.postername { color: #117743!important; } .postertrip { color: #228854!important; }
.omittedposts { color: #707070!important; }
.reply { background-color: #D6DAF0!important; border: 1px solid #BFC5DB!important; border-radius: 5px; }
blockquote, .reply blockquote { margin: .5em 1.5em!important; } hr { border-color: #BFC5DB!important; }
.doubledash { display: table!important; }
.spoiler { background-color: #C4C2EE!important; color: #C4C2EE!important; }
.spoiler:hover, .spoiler a { color: inherit!important; }
.reflink { font-size: inherit!important; font-weight: normal!important; }
.reflink a { color: inherit!important; }
.maintable { width: 100%!important; } .overlay-menu { border-color: #BFC5DB!important; border-radius: 0 0 5px 5px; }
.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreads { background-image: url("http://0chan.hk/css/icons/blue/icons.gif")!important; }
#boardlist_footer { clear: both; } #watchedthreads { background-color: #D6DAF0!important; border: 1px solid #BFC5DB!important; }
.topbar-left, .topbar-center, .topbar-right, .border-left, .border-right, .bottom-left, .bottom-center, .bottom-right,
.shareSite, .postarea p, .reflinkpreview .doubledash { display: none!important; }
.code_part { font-size: small!important; }
