css:
@-moz-document regexp("http:\\/\\/xkcd.com\\/([0-9]+|archive)?(\\/)?") {
	/* awesomexkcd | https://github.com/hbang/awesomexkcd | Apache 2 license */

	html {
		background: #ddd;
		padding: 40px;
	}

	body {
		font: 200 16px "Open Sans", "Helvetica Neue", sans-serif;
		font-variant: normal;
		padding: 20px;
		width: 80%;
		min-width: 200px;
		max-width: 750px;
		margin: 0 auto;
		background-color: transparent;
		position: static;
	}

	a {
		color: #04f;
	}

	img {
		max-width: 100%;
		height: auto;
	}

	.box {
		background: transparent;
		border-width: 0;
	}

	.bg {
		display: none;
	}

	#topContainer {
		width: auto;
		font-family: xkcd-Regular, sans-serif;
	}

	#topLeft {
		width: auto;
		float: none;
		text-align: center;
		padding: 0;
	}

	#topLeft ul {
		padding: 0;
	}

	#topLeft ul li {
		display: inline-block;
		margin-right: 10px;
	}

	#topRight {
		width: auto;
		float: none;
		text-align: center;
		padding: 15px 0;
	}

	#masthead {
		display: table;
		margin: 0 auto;
	}

	#masthead span {
		display: table-cell;
		vertical-align: middle;
	}

	#masthead a:hover {
		text-decoration: none;
	}

	#masthead a:before {
		font-size: 65px;
		color: #000;
		line-height: 50px;
		content: "xkcd";
	}

	#masthead img {
		display: none;
	}

	#slogan {
		padding: 0 0 0 20px;
	}

	#news {
		padding-top: 15px;
	}

	#middleContainer {
		width: auto;
		position: static;
		margin-left: 0;
		background-color: #fff;
		-webkit-box-shadow: 0 0 12px #555;
		-moz-box-shadow: 0 0 12px #555;
		box-shadow: 0 0 12px #555;
		border-radius: 5px;
		padding: 20px;
		word-wrap: break-word;
	}

	#ctitle {
		margin: 0;
		font-weight: 400;
		font-size: 32px;
	}

	ul.comicNav {
		margin: 10px 0;
	}

	ul.comicNav li {
		display: inline-block;
		margin: 10px 0;
	}

	#bottom {
		width: auto;
		padding: 25px 0 0 0;
	}

	#footnote {
		font-size: 10px;
		font-variant: normal;
	}

	#licenseText {
		width: auto;
		margin: 0;
	}

	@media only screen and (max-width: 600px) {
		html {
			padding: 0;
		}

		body {
			width: 90%;
		}

		#masthead span {
			display: block;
		}

		#slogan {
			padding: 0;
		}
	}
}

@-moz-document url-prefix("http://xkcd.com/archive") {
	#middleContainer {
		-webkit-column-count: 3;
	}
}
