css:
@-moz-document domain("geekhack.org") {
body
{
  background-color: #E7E7E7 !important;

  line-height: 1.4em;
  font-family: "Lucida Grande", "Trebuchet MS", Verdana, Helvetica, Arial, sans-serif !important;
  font-size: 13px!important;
  color: #333!important; 
}

h1.forumtitle img
{
  height:92px!important; 
  padding-left:510px!important; 
  margin-left: 10px!important;
  margin-top: 5px!important;
  width:0!important;background-image:url("http://aggiejy.com/geekhack/themes/light1/logo.png")!important
}

div#wrapper {

  background: #FFF;
  border: 2px solid #CCC;
  border-top: 2;
  border-bottom: 0;
  width: 980px !important;
  margin: auto;
  position: relative;
}

div#main_menu
{
  width: 100%;
  padding: 0!important;
  background: #efefef!important;
}

div#main_menu a.firstlevel
{
  border:none!important;
  color: #555!important;
}

div#main_menu a:hover
{
  color: #FFF!important;
}

div#main_menu li
{
  background:none!important;
}

div.user li.greeting 
{
  display:none!important;
}

#top_section 
{
  margin-bottom: -20px;
  background:none!important;
}

#upper_section 
{
  margin-bottom: -20px!important;
  margin-left: 20px;
  margin-top: 10px;
}

div#siteslogan 
{
  display: none;
}

#header 
{
  background: none !important;
  padding-left: 0px !important;
}

#header div.frame 
{
  background: none !important;
  padding: 0!important;
}

#content_section, #content_section div.frame
{
  background: none !important;
}

#footer_section
{
  background: none !important;
  border-bottom: 2px solid #CCC;
}

#footer_section div.frame 
{
  background: none !important;
}

/* Locked thread background color */
td.approvetbg, td.approvetbg2 
{
  background-color: #dfdfdf!important;
  color: #999!important;
}


.windowbg, .windowbg2 {
  background: none !important;
  color: #333;
}



div#boardindex_table table.table_list .windowbg2, 
div#boardindex_table div.topic_table .windowbg2
{
  background-color: #f2f2f2 !important;
}


table.table_list .windowbg, 
table.table_list #preview_body,
div.topic_table
{
  background-color: #e8e8e8 !important;
}

div.topic_table td.subject
{
  padding: 5px!important;
}

div.topic_table td.subject span a
{
  color: #555!important;
  font-size: 12px!important;
}

div.topic_table td.subject p
{
  color: #999!important;
}

div.topic_table td.subject p a
{
  color: #999!important;
}

.stickybg, .stickybg2
{
   background-color: #d4dce2 !important;
}


#preview_body
{
  background: none !important;
}

span.topslice, span.botslice
{
  display: none !important;
}

.stickybg, .stickybg2
{
   background-color: #d4dce2 !important;
}

p#whoisviewing 
{
  margin-bottom: 20px;
}

div.postarea, div.moderatorbar
{
  margin: 0 0 0 200px !important;
}

div.post_wrapper
{
  border-top: dashed 1px #CCC;

  
  background-image: url("http://aggiejy.com/geekhack/themes/light1/dash.png") !important;
  background-repeat:repeat-y !important;
  
}

.navigate_section ul
{
    border-top: dashed 1px #CCC!important;
}

.description, .description_board, .plainbox 
{
  background-color: #efefef !important;
}

div.signature 
{
  
  display: none!important; 
}
  
.keyinfo {
  width: 300px!important;
}

div.messageicon
{
  display: none!important;
}

div.keyinfo h5
{
  display: inline-block;
  float: left;
}

div.keyinfo h5 a
{
  background: url("http://aggiejy.com/geekhack/themes/light1/link.png") no-repeat!important;
  display: inline-block!important;
  height: 16px!important;
  width: 16px!important;
  text-indent: 100%!important;
  white-space: nowrap!important;
  overflow: hidden!important;
  margin: 2px 5px 0 0;
}

div.keyinfo div.smalltext
{
  color:#ccc!important;
  font-size: x-small;
  float: left;
  text-align: left;
  padding-top: 2px !important;
  width: 200px!important;
}

div.keyinfo div.smalltext strong
{
  display: none;
}

div.moderatorbar div.smalltext a
{
  color:#999!important;
  font-size: x-small;
}

div.moderatorbar div.smalltext img
{
  display:none!important;
}

#forumposts .reportlinks
{
  
  display:none!important;
}

img.modifybutton 
{
  display:none!important;
}


div.post div.inner 
{
  border-top: none!important;
  padding-top: 0!important;
  margin-top: 0!important;
  margin-bottom: 10px;
}

#forumposts .reportlinks 
{
  color: #999;
  padding-bottom: 2px !important;
}



table.table_list 
{
  border-spacing: 1px !important;
}

table.table_grid td
{
  padding: 0px !important;
  padding-top: 3px !important;
  padding-bottom: 3px !important;
}



div.title_barIC
{
  background: none !important;
  background: #CCC !important;
  
}



div.cat_bar a.collapse, div.cat_bar img#upshrink_ic
{
  display: none;
}

div.cat_bar 
{
  background:none!important;
  padding-top: 5px;
  margin-top: 10px!important; 
  border-top: 1px dashed #d9d9d9  
}

div.cat_bar h3 span#author 
{
  display:none!important;
}

div.cat_bar h3 img
{
  display:none!important;
}

div.cat_bar h3 
{
  font-family: 'Avenir Next', "Trebuchet MS", Arial, Helvetica, sans-serif;
  font-weight: 600;
  color: #3F3F3F;
  font-size: 18px;
}

div.title_barIC h3.titlebg a, div.title_barIC h3.titlebg, div.title_barIC h4.titlebg, div.title_barIC h4.titlebg a
{
  color: #333;
  font-weight: normal;
  font-size: 1.1em !important;
}

div.title_barIC h4.titlebg 
{
  background: #CCC !important;
}



li#button_irc, li#button_wiki, li#button_donate, li#button_recenttopics
{
  display: none !important;
}



img.avatar
{
    max-width: 64px !important;
}

li.blurb
{
  display: none !important;
}

li.profile
{
  display: none !important;
}

li.title
{
  display: none !important;
}

.postarea, .moderatorbar 
{
  margin: 0 0 0 10em !important;
}

.poster 
{
  width: 160px !important;
  margin-top: 10px!important;
  text-align: center!important;
}

.poster h4
{
  font-size: 12px;
}

.poster h4 img
{
  display: none;
}


blockquote.bbc_standard_quote 
{
  background-color: #f9f9f9;
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote 
{  
  border: 1px dashed #d9d9d9  
}

blockquote.bbc_alternate_quote 
{
  background-color: #efefef;
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote
{
  font-size: 11px!important;
}

.windowbg span.topslice, .windowbg span.topslice span, .windowbg span.botslice, .windowbg span.botslice span
{
    background: none!important;
}



.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
  background: none!important;
  color: #333!important;
}


ul.quickbuttons li
{
  background:none!important;
  margin: 0!important;
}

ul.quickbuttons li a
{
  padding:0 0 0 10px!important;
  color: #999!important;
  font-weight: normal;
}

ul.quickbuttons
{
  margin:0!important;
}


h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a 
{
  color: #333!important;
}

tr.catbg th a:link, tr.catbg th a:visited, tr.catbg2 td a:link, tr.catbg2 td a:visited 
{
   color: #333!important;
}

h3.catbg a.unreadlink
{
  font-size: 12px!important;
  color: #333!important;
}

tr.whos_viewing td.smalltext
{
  
}

tr.whos_viewing td.smalltext
{
  font-size: 10px!important;
    color: #999!important;
}



tr.whos_viewing td.smalltext a
{
    color: #999;
}

div.topic_table tr.catbg
{
  display: none!important;
}

#forumposts .post_separator {
  clear: both!important;
  height: 0!important;
  display: block!important;
}
}
