css:
@-moz-document domain("news.xinhuanet.com") {

#top,#header,/*头部*/
.sidebar,.c_right,/*侧栏*/
.set_font, /* A- A+ 按钮*/
#hl-Show,#Extend,#contextual,#edit,#yjfollowdiv,#selection, /*正文后面的部分*/
.share,#bdshare, /*分享*/
.yzyd, /*拓展阅读*/.wyak, /*网友爱看*/ .kjlj, /*快捷链接*/ .ad_left1,.ad_left2,.pinglun /*广告位*/
{display:none !important;}

body { background-color: #dfdfdf !important;}

/*拉宽正文*/
#main,.c_left {width: 100% !important;}

/*分享*/
#shareshow {background-image: none !important;}

/*正文标题*/
h1#title {
  font-size: 28px !important; 
  font-family: 方正宋黑_GBK,文泉驿微米黑,黑体 !important;
}

/*正文*/
p{
  text-indent: none !important;
  font-size: 26px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important;
}
p strong {
  font-family: tahoma,方正宋黑_GBK !important;
  color: #CA011E !important;
}

}
