/*
 * theinquirer.net - Bare To The Bone - Default Edition
 *
 * Author	 : Birkoff
 * Updated	 : July 15, 2008
 * Description	 : theinquirer.net stripped 'Bare To The Bone', re-built in original INQ colours
 * Usage      	 : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */ 


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theinquirer.net") {
html, body, #inq-header {
margin:0 !important;
padding:0 !important;
}

#vnu-layout {
color: #000 !important;
font-size: 12pt !important;
margin: 0px auto 0px auto !important;
padding:0 !important;
position: relative !important;
width: 800px !important;
background-color:#fff !important;
}

#intro, .hE, #subscribeForm, #secondary, #sponsorBar, #sidecontent, .clearfix {visibility:hidden !important;}

.searchBar, #topadvert, #externalTopad, .siteLinks {display:none !important;}

#inq-footer{padding:0px !important;}

#copyright{
background-color:#cc0000 !important;
padding:0.5em !important;
width:800px !important;
}

.articleList, #intelliTXT {
background-color:#fff !important;
color:#000 !important;
width:800px !important;
visibility:visible !important;
}
.articleList .articleColumn1, .articleList .articleColumn2, .articleList .articleColumn3 {
float:left !important;
padding:10px !important;
margin:0 !important;
}

.story, .title, .subtitle, .byline, #intelliTXT, #comments, .pagetools {
visibility:visible !important; 
display:inline !important;
}

.headerInfo {
margin: 0 0 12px 0 !important;
padding:2px 0 2px 0 !important; 
clear:both !important;
font-size:0.8em !important;
color:#fff !important;
}

.headerLogo h1 {
width: 800px !important;
height: 104px !important;
margin:0px !important;
border:none !important;
background-position:center !important;
}
.headerLogo h1 a {width:800px !important;}

}
