css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {
  background: 
url('http://25.media.tumblr.com/df0f9379b3ac41e3c39d5eb971db5db8/tumblr_mot3oeb8cX1sp4u0io3_1280.png') top left fixed no-repeat,
url('http://25.media.tumblr.com/c3aba280d2e9d268f921c04bcd27fdfb/tumblr_mot3oeb8cX1sp4u0io5_1280.png') bottom left fixed no-repeat,
url('http://i1225.photobucket.com/albums/ee393/Chal_Seldyn/Random%20pictures/privatebilly_zps225ff975.png') top right fixed no-repeat,
 url('http://i1225.photobucket.com/albums/ee393/Chal_Seldyn/Random%20pictures/billyheD_zps86747027.png') bottom right fixed no-repeat,
url('http://25.media.tumblr.com/8ce6c81a9559b969fe67bb310bbc229b/tumblr_mot3oeb8cX1sp4u0io8_400.png') right fixed no-repeat,
url('http://25.media.tumblr.com/4c8621468be35e1a253da3215c169a41/tumblr_mot3oeb8cX1sp4u0io7_400.png') left fixed no-repeat,
url('http://cdn.howtogeek.com/wp-content/uploads/2012/12/Plain-Black-Wallpaper.png') repeat;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:transparent !important;}

#right_column a#dashboard_switch_blog_menu_current:hover {background:#fff;}
#nav .nav_item.active{background-color:#fff;}
#left_column #posts .post .permalink{background-color:#fff;}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #fff !important;}
.notification_type_icon{display:none !important}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: #b7d9fb !important; 
	background: #333333 !important;}
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#b7d9fb !important;  
	background-image:none !important;
	border:0px !important}
div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#99b4cf!important;
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}
#auto_pagination_loader
	{background: #ffffff !important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important;}
#left_column #posts .notification {
background-color:#666 !important;
color:#b7d9fb !important;
border-bottom-color: #b7d9fb !important}

#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}


.permalink {background: none !important;
width: 16px !important;
height: 16px !important;
  background: #004182) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 10px !important;
  margin-top: 3px !important;
  margin-right: 4px !important;}


.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
}
