css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {

#links, #slashboxes, #commentwrap, #footer, .btmnav, #fad1, #indexhead
{
  display: none !important;
}

#frame, #topnav, #wrapper, #user-section, #contents, #commentlisting, li.comment
{
  margin: 0em !important;
  padding: 0em !important;
}

li.comment
{
  margin-top: 1em !important;
  border: none !important;
}

body
{
  padding: 0em;
}

.copyright
{
  margin: 0em !important;
}

.commentSub
{
  padding: 0em;
}

.nbutton, .nbutton p
{
  margin: 0em;
  padding: 0em;
  background: none !important;
}

.nbutton p b a
{
  background: #999 !important;
}

#articles
{
  margin-right: 0em !important;
}

#contents
{
  margin-left: 0em !important;
  margin-top: 0.5em !important;
}

#article
{
  margin: 0em 1em 0em 1em !important;
}

.generaltitle
{
  clear: both;
}

body  {
width:800px !important;
margin: 0px auto 0px auto !important;
padding-bottom:0px !important;
}

.current {display:none;}

}

@-moz-document url("http://slashdot.org/") {

.article, .tags, .storylinks, .briefarticles
{
  margin: 0em 1em 0em 1em !important;
}

.storylinks, .briefarticles
{
  margin-bottom: 1em !important;
}

}
