css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theoldreader.com") {


div.sort-item.prevent ul {
    display: none;
}

div.span3.subscribe-fixed-top {
    display: none;
}

/*div.well.sidebar-nav> :first-child {
    display: none;
}

div.well.sidebar-nav> :nth-child(2) > li {
    display: none;
}*/

ul.nav.nav-list.feeds-list.ui-sortable {
    display: none;
}

li.sort-item.social {
    display: none;
}

div#subscribe {
    display: none;
}

div.span3.subscribe-fixed-top {
    display: none !important;
}

div.sort-item.prevent {
    display: none !important;
}

div#sidebar {
    width: 100% !important;
/*   z-index: 1 ; */
    left: 5px !important;
}

div.slide{
	height: 80px;
}

ul.nav.nav-list {
	float: left;
}

ul.nav.nav-list li {
    display: inline-block;
	margin-top: 6px;
	margin-right: 5px;
}

div.well.sidebar-nav {
    overflow-x: auto;
    padding-top: 0;
    white-space: nowrap;
    min-width: 200px;
    padding: 10px 0 0 0 !important;
    height: 60px;
}

div.well.sidebar-nav> :nth-child(2) {
    width: 200px;
    float: left;
}

div.sort-item {
    display: inline-block;
    width: 200px;
}

div.sort-item.prevent {
    display: inline-block;
    width: 200px;
}

div#main {
    left: 0 !important;
    width: 100% !important;
/*    z-index: 0 !important; */
    margin-top: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    top: 80px !important;
    height: 95.5% !important;
    font-size: medium;
}

div#main div.slide {
    padding: 0 !important;
	height: 100%;
/*    margin-top: 50px; */
}

div.static {
	padding: 0;
}

div.floating {
    z-index: 5;    
    bottom: 0;
    position: absolute;
    width: 100%;
    height: 105px;
    background-color: #FFF;
    left: 2px !important;
}

form.pull-left {
	width:50%;
}

form.pull-right {
	width:50%;
}

div.floating a.btn {
    display: block;
    height: 64px;
    width: 12%;
    overflow: hidden;
}

div.floating button.btn {
    height: 70px;
    width: 18%;
    padding-top: 15px;
    margin-left: 5px !important;
    float: right;
}

}
