@-moz-document domain("cloud.feedly.com") {

    .websitecallforaction { display: none; }

    /* green ref: 82BD1A, medium green: 527B08, dark green: 30460A */
    body { background: none repeat scroll 0% 0% rgb(26, 26, 26) !important; }

    /* manage full screen size */    
    #feedlyFrame  { width: auto !important;  }
    #feedlyPart { width: 100% !important;  }   
    #feedlyPage { width: auto !important;}  
    #mainArea { width: auto !important; margin-right: 100px !important; }  
    .summary { max-width: 80% !important;}    
    .frameActionsTop { height: 13px !important; }
    .area, .inlineFrame { 
        background-color: transparent;
        background: none repeat scroll 0% 0% rgb(26, 26, 26) !important;
        border: 0px solid rgba(51, 51, 51, 0.4) !important;
    }
    div.area {background-color: transparent;}

    .area { background-color: transparent !important;}

    /* home page right sidebar bar */
    #sideArea .feedIndexTitle {color: #999 !important;}
    #sideArea { position: absolute !important; margin-right: 10px !important; overflow: visible !important; right: 0 !important;}
    .fusionAdLink {border-color: #333 !important; background-color: #222 !important;}

    .u4Entry { margin-bottom: 18px !important; max-width: none !important;}

    /* condense layout: reduce unused space */
    #feedlyPart  {margin-top: 20px !important; }
    #feedlyPart0.area  {padding-left: 5px !important; }
    #feedlyPageHeader { margin-top: 0px !important;}
    h2 {margin-top: 18px !important; margin-bottom: 12px !important;}
    .condensed .inlineFrame { padding-left: 40px !important; padding-right: 40px !important; }    
    #section0 > div > div { height: 0px !important; }

    /* add website floating panel */
    #storeHolder.picturePicture {  background-color: #1A1A1A !important; color: #ccc !important; border-color: #222 !important;}
    .wide #storeHolder.picturePicture { margin-left: 80% !important;}
    #store .suggestion { border-bottom-color: #222 !important; height: 16px !important; }  
    #storeFrame { background-color: #1a1a1a !important; }
    /* ::-moz-placeholder { background-color:#1a1a1a !important; }  firefox 19+ */
    #store .separator { background-color: #527B08 !important; }
    #store .result { color: #82BD1A !important; border-bottom-color: #333 !important;}

    /* edit/remove feed */
    #feedlyDialogFramePart { background-color: #1a1a1a !important; color: #ccc !important; }
    .hAction.secondary, .hAction.tertiary { background-color: #222 !important; }
    .fieldLabel { color: #82BD1A !important;}
    .checkboxLabel { color: #999 !important;}


    #feedlyCenter {background: none repeat scroll 0% 0% rgb(26, 26, 26) !important;}

    .unread {color: #ccc !important;}
    .read {color: #666 !important;}
    .home {color: #999 !important;}
    .notSubscribed .read {color: #ccc !important;} 

    .u0Entry {border-color: #303030 !important;}
    .u0Entry:hover {background: none repeat scroll 0% 0% rgb(51, 51, 51) !important;}
    .u0Entry.lastModified { color: #82BD1A !important; }

    /* saved entries */
    .u0Entry.quicklisted { background-color: #1A1A1A !important; border-color: #30460A !important; }

    /* font size and colors */  
    #feedlyTitleBar h1 {font-style: normal !important; color: #82BD1A !important;}
     h1, h2, h3 { color: #82BD1A !important;}
    .summary {color: #999 !important;}
    .entryTitle { color: #82BD1A !important;}    
    .entryBody {color: #ccc !important; margin-top: 5px !important;}
    .entryBody td {max-width: none;}
    .action { color: #82BD1A !important; }
    .nonEmpty { color: #ccc !important; }
    .viewerIcon { background-color: #1a1a1a !important; color: #999 !important;}
    #who, #systemBar { color: rgba(252, 252, 252, 0.85) !important; }
    .fusionAD > div { color: #82BD1A !important;}
    .metadata > span {color: #527B08 !important; }
    .categoryUnreadCount {opacity: 1.0 !important; }
    .entryBody .summary b, .entryBody .content b { color: #ccc !important; }
    .empty {color: #999 !important;}

    /* homepage */
    .visual { border-color: #222 !important;}

    /* reccomendation */
    .u11Entry { background-color: #30460A !important; }
    .topRecommendedEntry { width: 300px !important; }

    /* left sidebar */
    #feedlyTabsHolder { color: rgba(255, 255, 255, 0.4) !important; margin-top: 5px !important; }
    #feedlyTabsHolder .target.selected { color: #82BD1A !important;  background-color: #1a1a1a !important; }          

    /* top floating bar */
    #floatingBar {background-color: #1a1a1a !important; border: 1px solid #222 !important;}
    #floatingBar h1 {border-bottom-color: #1a1a1a !important; color: #82BD1A !important; }
    #floatingTitleBar {color: #ccc !important;}

    /* feed content */
    .u100Entry {background: none repeat scroll 0% 0% rgb(26, 26, 26) !important; min-height: 80px !important;}         
    .u100Entry .title { margin-bottom: 14px !important;}
    .u100frame.selectedentry { background-color: transparent !important;}

    .entryHolder {background: none repeat scroll 0% 0% rgb(26, 26, 26) !important;}

    .metadata {color: #666 !important;}

    .topWikiWidget {border-bottom: 1px solid #222 !important; margin-bottom: 5px !important; padding-bottom: 5px !important;}
    .wikiWidgetAction {
        background-color: #1a1a1a !important ;
        visibility: hidden;
        width: 0px;
        height: 0px;
    }

    /* organize page */
    .subscriptionContentsHolder { background-color: #1A1A1A !important; border: 1px solid #222 !important; }
    .subscriptionContentsHolder:hover { border: 1px solid #82BD1A !important; }

    /* preference page */
    .preferencesItemLabel { color: #ccc !important; }
    .preferencesItemDescription { color: #999 !important; }
    .preferencesPanel label { color: #999 !important; }

    /* icons management */

    .toBeTagged {background-color: #1A1A1A !important; }

    #feedlyTabsHolder .icon { opacity: 0.4 !important }
    #feedlyTabsHolder:hover .icon { opacity: 1.0 !important }    

    /* twitter fix */
