@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jupiterbroadcasting.com") {
html, body {
background:#222 !important;
}

#contentwrap {
color:#FFF !important;
background:#222 !important;
}
#contentwrap a, .itemdesc, .gallerydate, .itemdesc,
 .itemtitle a, .description, #footer, #footer a, a, p,
 h1, h2, h3, h4, h5, .info h2{
color:#FFF !important;
}

#menu-main-menu, #menu-main-menu a {
color:#FFF !important;
background:#222 !important;
}
#menu-main-menu a:hover {
text-decoration:underline !important;
color:#FFF !important;
background:#000 !important;
}
#header, #contentwrap, #pagewrap, .description, .first-child,
 .submenu {
background:#222 !important;
}
