css:
@-moz-document domain("clients.shoutlet.com") {
body {
  background-image: url('http://shoutlet.com.s1111.gridserver.com/img/noisydark.png');
}
#header {background: url('http://shoutlet.com.s1111.gridserver.com/img/noisyblack.png') repeat;}
#header {height:35px;}



#sidebar {
  	margin:0;
  	width:15%;
}

#container .main-content {
	width:85%;
  	padding:0;
  	min-height:700px;
  	margin:0;
  	border-radius:0;
  	box-shadow: 0px 0px 40px rgba(0,0,0,.3);
  	background: url('http://cl.ly/image/1E1P2q442J46/noisy-texture-100x100-o5-d10-c-e9eaed-t0.png') repeat #e9eaed
}


#calendar-icon a, #library-icon a, #searchbox a, #help-icon a {
  	transform: scale(.8,.8);
	-ms-transform: scale(.8,.8); /* IE 9 */
	-webkit-transform: scale(.8,.8);
	opacity:.7;
}

#main-navigation li a {
	font-family:Helvetica;
  font-size:12px;
  line-height:35px;
  padding:0 10px;
  background:none;
  height: auto;
 	text-transform: capitalize;

}
#profile-information .logo {
	height:auto;
    width:auto;
  right:0;
}
#profile-information .logo img {
	height: 25px;
	width: 25px;
	margin-top: 5px;
}
#logo, #homepage-button, #main-navigation, #header-container, #profile-information {
  height:35px;
}
#canvas-toolbar {
  top:35px;
}

#profile-information .logo.active {
	border:0;	
  	right:0;
}
#profile-menu {
  top:35px;
  border-top-width:0;
}
#profile-information .info {
	padding:6px 50px 0 0;
  	font-weight:normal;
  	color:rgb(100,100,100);
  	font-size:11px;
  	transition: color 200ms ease-in-out;
  	bottom:auto;
}
#profile-information .clientname {
	font-family:Helvetica;
  	font-weight:normal;
  color:rgb(180,180,180);
  font-size:11px;
}
#profile-information .info:hover .clientname {
  text-decoration:none;
  color:white
}
#profile-information:hover .clientname {
  text-decoration:none;
}
#profile-information .arrow {
	right:35px;
  opacity:.5;
}

#container.nomenu .main-content { width:100% !important; } 
#switchboard, #switchboard-header {
  	width: auto;
	border: 0;
	margin: 0 20px;
}
#switchboard .channel{   	
  border:1px solid #d3d6db;
  border-radius:3px;
  background:white !important;
  position:relative;
  margin:0 0 5px 0;
}
#switchboard .channel .info {}
#switchboard .channel .previous-event {} 
#switchboard .channel .next-event {float:right;}
#switchboard .channel .events {
  	position: absolute;
	left: 358px;
	right: 30px;
	width: auto;
}
#switchboard .channel .add-large { margin: 50px auto; position:relative; left:auto; top:auto; }
  
.data-container { margin-top:0px }
.post-container {padding:5px 0 10px 0;}

/*   
.post-container {
	width:46%;
    margin:0 4% 0 0;
    float:left;
}
.actions {
	padding:0 120px 0 0 !important;
}
*/

.who-when {
	position:absolute;
    bottom:35px;
}
.who-when:hover {opacity:1}
.who-when a {color:#888;}
.who-when a:hover { color:#1b96d3; text-decoration:none;}

.who-when, .actions {
  opacity:.3;transition: opacity .15s ease-in-out; 
  text-transform:capitalize;
  padding:0 10px;
  left:0;
  right:0;
}
.post-details .actions {
  	opacity:.7;transition: opacity .15s ease-in-out; 
  	position:absolute;
    bottom:0px;
  	border-top:1px solid #d3d6db;
  	margin:0;
  	padding:0;
  	background: rgb(248, 248, 248);
	max-height: 30px;
}
.post-details .actions li {
  margin:0;
  padding:0;
  height:30px;
}
.post-details .actions li a {
  line-height:20px;
  display:block;
  padding:5px 7px;
}
.post-details .actions:hover {opacity:1}
.post-details .actions a {color:#888;
  transition: color 200ms ease-in-out; }
.post-details .actions a:hover { color:#1b96d3; text-decoration:none;}
img.avatar {
  	width: 35px;
  	height: 35px;
	margin-left: 0px;
}
img.avatar.parentpost {top: 15px; left: 15px;}
.post-details { 
  	margin-left:45px; 
  	border:1px solid #d3d6db;
    border-radius:3px;
    background:white;
    font-family:Helvetica;
    padding:10px;
  	padding: 10px 10px 60px 10px;
  	position:relative;
  min-height:auto;
}
.tag-container {
	float: none;
	position: absolute;
	right: 0;
	top: auto !important;
	bottom:30px;
  padding:8px 0;
}
.tag-container ul.tagslist {
  margin-top:0px;
  }
.tag-container ul.tagslist li {
  margin:0 0 0 3px;
  border-radius: 2px;
  background: rgb(247, 247, 247);
  padding: 0 2px 0 7px;
  color:rgb(180, 180, 180);
  transition: color 200ms ease-in-out;
  line-height: 15px;
  font-size: 10px;
  }
.tag-container ul.tagslist li:hover{
	color:rgb(80,80,80);
}

.tag-container ul.tagslist li a {
	float: right;
	height:0;
	width:15px;
  overflow:hidden;
	padding: 15px 0 0 0;
	display: block;
	background: url('http://shoutlet.com.s1111.gridserver.com/img/close.png') no-repeat center center;
  margin-left: 5px;
  opacity: .3;
}
.plus-button, .x-button, .image_modal a.close-modal, .main-section .panel a.close-modal, .main-section .panel a.modal-close {
	background: url('http://shoutlet.com.s1111.gridserver.com/img/close.png') no-repeat center center;  
}


.tag-container ul.tagslist li a:hover {
  opacity:1;
  }
.tag-container .tag-icon-container { border:0; }
.tag-container .tag-icon-container { margin-bottom:4px}

#container { 
  width:auto; 
  min-width:1000px; 
  padding-top:35px; 
}


/*  Sidebar  */


#side-navigation li, .navigation-menu li {
	width:auto;
  padding:0;
}
#side-navigation li .first.sub-navigation li, #side-navigation li .second.sub-navigation li {
  padding:0;
}
#side-navigation li a { 
  font-size:14px;
  text-shadow: 1px 1px 2px rgba(0,0,0,.4);
}
#side-navigation li .first.sub-navigation li a { 
	width:auto;
  	height:auto;
    padding:5px 5px 5px 55px;
  line-height:15px;
  font-size:12px;
  margin:0;
}
#navigation-indicator { top:8px !important; display:none; }
#side-navigation li a, .navigation-menu li a {
  width:auto;  
  border-radius:0;
  text-transform:capitalize;
  font-family:Helvetica;
  border:0;
  background:none !important;
  background-color:none !important;
  transition: background-color 200ms ease-in-out;
  color:#7c7c7c !important;
}
#side-navigation li a:hover, .navigation-menu li a:hover {
	background-color:rgba(200,200,200,.05) !important;
  color:#eee !important;
}
#side-navigation.dashboard li .icon, #side-navigation.reports li .icon, #side-navigation.settings li .icon {
	top:2px !important;
  	transform: scale(.7,.7);
	-ms-transform: scale(.7,.7); /* IE 9 */
	-webkit-transform: scale(.7,.7);
	opacity:.5;
}
#side-navigation.dashboard li.active .icon {
	opacity:1;
}
#side-navigation.dashboard li:hover .icon {
	opacity:1;
}
#side-navigation.dashboard li.facebook.active .icon {
	opacity:1;
}
#side-navigation li a.active, #side-navigation li a.active:hover, .navigation-menu li a.active, .navigation-menu li a.active:hover, .modal-content #side-navigation.switchboard li.active a, .modal-content #side-navigation.switchboard li:hover a, #side-navigation .sub-navigation a.active {
  color:#1b96d3 !important;
  background:none !important;
  border-right: 3px solid #1b96d3;
}
#side-navigation li.seperator.title, .navigation-menu li.seperator.title, #side-navigation li.seperator, .navigation-menu li.seperator {
	width:auto;
  text-indent:10px;
  margin:0;
  border:0;
  font-family:Helvetica;
  padding-top:20px;
}
#side-navigation li.seperator, .navigation-menu li.seperator {
  padding-top:30px;
}

#user-information .logo {
  height:40px;
  width:40px;
  top: 20px;
left: 15px;
}
#user-information .logo img {
  height:100%;
  width:100%;
  border-radius:0;
  border:1px solid #d3d6db;
}
#user-information .username {
	color:rgb(180,180,180);
  bottom: 25px;
}

#sidebar h2 {
	color:rgb(200,200,200);
  	margin-top:20px;
	font-family: Helvetica;
	text-transform: capitalize;
  	padding-bottom: 15px;
}

/*  Main Content  */

.main-content .main-section {
	margin:20px 0 0px 0;    
}
.shoutbox-container, .shoutbox-container.grey {
	width:auto;
    margin:0;
    padding:20px;
 	background:white;
    box-shadow: 0px 0px 0px 0px rgba(0,0,0,.3);
	border-radius:0;
  }
  .shoutbox-container textarea, .shoutbox-container input[type="text"].shout {
    width:99% !important;
    }
.character-count {right:20px;top:20px}
.button-container {  }
.button {border-radius: 2px;}

.feed-container { width:auto; padding:10px 20px 20px 20px;}
.content p, .post-details, p.default, .main-content li, .main-content p {max-width:none;width:auto;}
.main-content p { margin-right:0px; font-size:15px;}
.search-results { padding-right: 20px; display:none; }
.main-section .panel { padding-right: 20px; border:0; }
.modal .main-section .panel { padding-right: 0px; }
#inline-modal .button-container, .hover-modal .button-container, #help-window .button-container, .modal .button-container {
	margin:30px -10px -10px -10px !important;
    padding:10px;
   	background: rgb(248, 248, 248);
   -moz-box-shadow:    inset 0px 2px 6px rgba(100,100,100,.05);
   -webkit-box-shadow: inset 0px 2px 6px rgba(100,100,100,.05);
   box-shadow:         inset 0px 2px 6px rgba(100,100,100,.05);
}
.modal .button-container .button {
  margin:0 0 0 5px;
        
 }
ul.stats {
  top:auto;
  bottom:0;
  right:10px; 
  z-index:10;
}

#side-navigation li .sub-navigation {padding-bottom:10px;}

.post-details ul.stats li {
  	background:none;
  	border:none;
    font-weight:normal;
  	line-height: 30px;
	padding: 0 5px;
}
.post-details ul.stats li, .post-details ul.stats li.likes:before, .post-details ul.stats li.comments:before {
  	color:rgb(210, 210, 210);
    opacity:1;
    font-size:12px;
	font-weight:normal;
}
.post-details ul.stats li.likes:before, .post-details ul.stats li.comments:before {
  content:"Likes ";
  background:none;
  display:inline;
  width:auto;
  padding-right:5px;
}
.post-details ul.stats li.comments:before {
  content:"Comments ";  
}       
ul.stats li.blue, .post-details ul.stats li.blue:before {
	color: #3494d1 !important;
}

/*  Dashboard   */

.activity-feed {width:auto; margin:0 20px;}
.activity-feed li.announcement {
  	border:1px solid #d3d6db;
    border-radius:3px;
    background:white;
    margin:5px 10px;
}
.activity-feed li.announcement span.icon {
  	transform: 	scale(.7,.7);
  	-ms-transform: scale(.7,.7); /* IE 9 */
	-webkit-transform: scale(.7,.7);
	opacity:.5;
}
.reportContainer {
  	float:left !important;
    clear:none !important;
  	width:33%;
}
#masterReportContainer, #masterReportContaineraggregated { padding:20px;}
  
.debug .main-section .icon, #container .main-content .main-section .icon { border:0 !important; margin-left:18px; }
.tag-container {
	top:10px;
    right:10px;
  }
  .tag-container .tag-icon-container {margin-top:0;}

.search-results.bottom {
    padding:20px;
}
#inline-modal, .hover-modal {
  background:white;
  border:0;
  border-radius: 2px;
	-webkit-box-shadow: 0px 10px 50px 0px rgba(0, 0, 0, .9);
	-moz-box-shadow: 0px 10px 50px 0px rgba(0, 0, 0, .9);
	box-shadow: 0px 10px 50px 0px rgba(0, 0, 0, .9);
}
.modal .main-section .icon {border:0 !important;}
.modal .main-section  .title {padding-left:0;}
.main-section .title, .main-section .subtitle {
  font-family: Helvetica;
  font-size: 14px !important;
  text-transform: capitalize !important;
  letter-spacing: 0 !important;
  color: rgb(65, 65, 65) !important;
  padding: 14px 0 0 10px;
  float: left;
  font-weight: normal;
}
.feed-data {
  padding:20px;
}

table.default tbody tr:nth-child(even), 
table.default tbody tr:nth-child(odd) {border-top:1px solid #d3d6db;}
table.default tbody tr:nth-child(odd) {background:white;}

/* Tables */

table.default {
	border:1px solid #d3d6db;
    border-radius:2px;
}

.whole.column, table.dataTable {
  width:100% !important;
    margin:0;
}
.reportModBody {width:auto;margin:0;padding:0 10px 10px 0;}
.reportModBody .highcharts-container {width:100% !important;}
#reports-note {
    padding:20px;
}

/*  Footer  */


#footer {
	width:auto;
  border:none;
  margin:50px 0;
}
    #footer a { text-transform:capitalize;}



.ui-corner-all, .ui-corner-top, .ui-corner-left, .ui-corner-tl, .ui-corner-bottom, .ui-corner-right, .ui-corner-left, .ui-corner-bl, .ui-corner-br, .ui-corner-tr { border-radius:2px !important; }
.main-section .panel .options.no-border {padding:10px 0px 0px 5px;}
.panel .options a.export-csv {margin:0 0px 0px 10px;padding: 10px 0 0 0;}
.ui-combobox-input {font-family: Helvetica !important;}


