css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://tumblr.com/dashboard"),url-prefix("http://www.tumblr.com/dashboard") , url-prefix("https://www.tumblr.com/dashboard") {

.selection_nipple {
    display: none !important;
}

#logo {
    height: 25px !important;
    width: 136px !important;
    background-position: -917px -2px;
    background-image: url("http://l4.yimg.com/dh/ap/default/130429/pc_icons_btns_sprite_0429_3pm.png");
}

#logo a {
    background-image: none !important;
}

.search_form_row {
    background-color: #ffffff !important;
}

.search_query{
    color: #000000 !important;
}

.search_query_submit {
    height: 22px !important;
    width: 22px !important;
    margin: -10px 0 0 6px !important;
    padding: 0px 4px 8px !important;
    background-image: url("http://l4.yimg.com/dh/ap/default/130429/pc_icons_btns_sprite_0429_3pm.png") !important;
    background-position: -755px -45px !important;
}

body {
    background-image: none !important;
    background-color: #FAFAFC !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background-color: #3F1C59 !important;
}

#right_column {
    overflow: hidden !important;
}

#right_column > ul,
.radar_controls {
    background-color: #3F1C59 !important;
}

#posts .post .permalink {
    background-image: none !important
}

#auto_pagination_loader{
    background-image: none !important;
    background-color: #3F1C59 !important;
}

.iconic > a {
    background-image: url("http://l4.yimg.com/dh/ap/default/130429/pc_icons_btns_sprite_0429_3pm.png") !important;
    background-size: 1230px 100px !important;
    width: 20px !important;
    height: 20px !important;
    margin: 4px 20px !important;
}

#inbox_button > a {
    background-position: -43px -66px !important;
}

#home_button > a {
    background-position: -22px -66px  !important;
}

#help_button > a {
    background-position: -273px -24px !important;
}

#preferences_button > a {
    background-position: -316px -66px !important;
}

#logout_button > a {
    background-position: 0px -44px !important;
} 

.post_full .post_permalink {
    background: url(data:image/png;base64,) !important;
}

}

