css:
/* Social-in-Work */
@-moz-document url("chrome://browser/content/browser.xul") {
tab[image*="fbstatic"] .tab-icon-image {
margin-left: 16px !important;
width: 0 !important;
}
tab[image*="fbstatic"] .tab-text {
visibility: hidden !important;
}
}


@-moz-document domain("facebook.com") {
/*General Properties.*/
img {
opacity: 0.2 !important;
}
img:hover, div:hover > img, a:hover > img {
opacity: 1 !important;
}
.stageWrapper * {
background-color: transparent !important;
}
* {
font-family: serif !important;
}

._s0:only-child, ._s0, ._29h img {
display: fixed!important;
border-radius: 100% !important;
border: 1px solid #fff !important;
box-shadow: 0px !important;
}
UL#pageNav img {
display:none
}

/*FB bar*/

.slim #blueBar, #blueBar{
position: relative;
border-bottom: 1px solid;
background-color: transparent !important;
background-image: -webkit-gradient( linear, left top, left bottom, from(#76aaef), to(#bbd5f6) ) !important;
background-image: -moz-linear-gradient(top, #76aaef, #bbd5f6)!important;
background-image: -ms-linear-gradient(top, #76aaef, #bbd5f6)!important;
background-image: -o-linear-gradient(top, #76aaef, #bbd5f6)!important;
background-image: linear-gradient(top, #76aaef, #bbd5f6)!important;
border-color: #bbd5f6 !important;
}

.hasSmurfbar #blueBarHolder #jewelContainer {
    height: 40px;
    left: -810%;
}

._578- ._585- {
    left: 10%;
}
#facebook .hasSmurfbar #blueBarHolder ._578- #pageLogo a {
    left: 225%;
}

.hasSmurfbar #blueBarHolder .-cx-PUBLIC-fbFacebarContrast__medium #pageLogo {
    left:450px;
}
.hasSmurfbar ._578w .fbFacebar, .hasSmurfbar ._578- .fbFacebar{
    left: 150px;
}

/*Right side*/

#pagelet_ego_pane_w {
display: none !important;
}

#pagelet_rhc_footer {
display: none !important;
}

#pagelet_side_ads {
display: none !important;
}

.fbTimelineSideAds {
display: none !important;
}

.ego_column {
display: none !important;
}

/*Left side*/

#pagelet_welcome_box{
display:none !important;
}

}
