css:
@namespace url(http://www.w3.org/1999/xhtml);


@font-face {
  font-family: 'trolltype';
  src: url("http://trolltype.mspa.sudrien.net/trolltype.eot?v=10&captcha=TBLRDASH");
  src: url("http://trolltype.mspa.sudrien.net/trolltype.eot?v=10&captcha=TBLRDASH#iefix") format("embedded-opentype"), url("http://trolltype.mspa.sudrien.net/trolltype.woff?v=10&captcha=TBLRDASH") format("woff"), url("http://trolltype.mspa.sudrien.net/trolltype.svg?v=10&captcha=TBLRDASH#trolltype") format("svg");
  font-weight: normal;
  font-style: normal; }


@-moz-document domain("www.tumblr.com") {


    body {
        font: 24px/1.4 'trolltype' !important;
        direction: rtl;
        }
        
    input, select {
        font: 24px/1.4 'trolltype' !important;
        }
        
    #posts .post_footer_links{
        font: 20px/1.4 'trolltype' !important;
        }
        
    #tabs .tab {
        font: 24px/1.4 'trolltype' !important;
        }
        
    .chrome{
        font: 20px/1.4 'trolltype' !important;
        }
        
        
    /* make text right to left */
    *:before {
        content: "\202E";
        }
        
    *:after {
        content: "\202C";
        }
    
    /* make blockquotes right to left */
    #posts .post_content blockquote {
        border: 0 !important;
        border-right: 4px solid #DCDCDC !important;
        margin: 10px 0 20px 10px !important;
        padding: 0 !important;
        padding-right: 15px !important;
        }
    }
