css:
@-moz-document url("https://*.twitter.com"), url("http://*.twitter.com"), domain("twitter.com") {
.dashboard {
  position:fixed;
}

.dashboard *,.content-main *, .topbar *, .expansion-container *,.content-main, .content-narrow, .page-canvas, .permalink, .profile-card.profile-header .avatar,
.module ul:first-child li:first-child .list-link, .module .flex-module:first-child, .module .contrast-flex-module:first-child,
.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, 
.module .flex-module:last-child, .module .contrast-flex-module:last-child, .btn, .modal-header, .modal-content,
input, textarea, div[contenteditable="true"], select {
  border-radius:0!important;
}
  
.global-nav {
  background: none!important;
  background-color: #000000!important;
}

.nav>li>a {
  line-height: 16px;
}

.nav .active a {
  background-color: #353535;
  background-image:none;
  text-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;
}

.open.stream-item {
  margin-bottom: 1px!important;
  margin-top: 2px!important;
}

.global-nav .search-icon {
  top: 1px;
}

.profile-card.profile-header {
  float: right;
  width: 561px!important;
  position: relative;
  border: 0;
  border-radius: 0!important;
  margin: 1px 0px 2px 0!important;
}    

.wrapper-profile .profile-card.profile-header .profile-header-inner {
  background-size: 100%;
  background-position: center;
}    

.profile-header-inner-overlay, .modal-content.clearfix, .modal-body.profile-modal {
  width: 561px!important;
}
    
 
.btn { 
  background-image:none; 
}    

#profile_popup .modal {
  width: 561px!important;
}
.profile-card.profile-header .profile-header-inner {
  background-size: 100%;
  background-position: center;
}

.content-header .header-inner, .content-no-header .no-header-inner {
  min-height: 32px;
}
          
.wrapper, .wrapper-narrow, .wrapper-permalink {
  width: 865px!important;
  padding:42px 0 15px!important;
}
.wrapper {
  background : none!important;
          }
.content-main {
  width: 561px!important;
  margin: 0!important;
}
.module {
  margin-bottom: 1px!important;
}

.stream-item, .unfocusable-stream-item, .open.stream-item {
  border-left:0;
  border-right:0;
}

.profile-card.profile-header .profile-picture, .module, .stream-item, .content-header, .content-no-header, .stream-item.open {
  border : 0!important;
}

.module.site-footer {
  display : none;  
}
}
