css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jsbeautifier.org") {

  /* *** Actual styling *** */
  .CodeMirror {
    line-height: 1.2em !important;
    height: 600px !important;
  }
  .CodeMirror div.CodeMirror-cursor {
    border-left: 2px solid black !important;
  }

  /* *** Override editor's font */

  /*@ import url(http://fonts.googleapis.com/css?family=Inika:400,700&subset=latin,latin-ext);*/
  @font-face {
    font-family: 'Inika';
    font-style: normal;
    font-weight: 400;
    src: local('Inika'), url(http://themes.googleusercontent.com/static/fonts/inika/v1/fW1K6RPrXXXU00ohNG2ccw.woff) format('woff');
  }
  @font-face {
    font-family: 'Inika';
    font-style: normal;
    font-weight: 700;
    src: local('Inika Bold'), local('Inika-Bold'), url(http://themes.googleusercontent.com/static/fonts/inika/v1/nhLyQa1JUjKKAGFakDXqPfesZW2xOQ-xsNqO47m55DA.woff) format('woff');
  }
  .CodeMirror { font: normal 0.9em Inika, Consolas, monospace !important; }

  /*
  @ import url(http://fonts.googleapis.com/css?family=Droid+Sans+Mono&subset=latin,latin-ext);
  .CodeMirror { font: normal 1em "Droid Sans Mono", Consolas, monospace !important; }

  @ import url(http://fonts.googleapis.com/css?family=Inconsolata:400,700&subset=latin,latin-ext);
  .CodeMirror { font: normal 1em Inconsolata, Consolas, monospace !important; }

  @ import url(http://fonts.googleapis.com/css?family=Oxygen+Mono&subset=latin,latin-ext);
  .CodeMirror { font: normal 0.9em "Oxygen Mono", Consolas, monospace !important; }
  */

}
