css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("next-episode.net") {

	* {
		font-family:Verdana!important;
	}
	body {
		background:hsl(120,40%,20%)!important;
	}
	.main_table {
		border-color:hsl(120,80%,10%)!important;
		box-shadow: 0 0 15px 5px hsl(120,80%,10%);
		background:white!important;
	}

	.pickAShow a {
		border: 1px solid hsl(153,52%,62%)!important;
		background: hsl(152,54%,42%)!important;
	}

	.td4 {
		background:hsl(120,48%,33%)!important;
	}
	td.td4:nth-child(2) > a:nth-child(1) img,
	#header > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > a:nth-child(1) img {
		display:none!important;
	}
	td.td4:nth-child(2) > a:nth-child(1):after {
		content:"next-episode.net";
		color:#fff!important;
		font: bold 2em Courier New!important;
		margin-right:2em;
	}
	td.td4:nth-child(2) > a:nth-child(1) {
		text-shadow:
			-3px -3px 1px hsl(120,56%,40%), /*up left*/
			0px -3px 1px hsl(120,56%,40%), /*up*/
			3px -3px 1px hsl(120,56%,40%), /*up right*/
			3px 0px 1px hsl(120,56%,20%), /*right*/
			3px 3px 1px hsl(120,56%,20%), /*down right*/
			0px 3px 1px hsl(120,56%,20%), /*down*/
			-3px 3px 1px hsl(120,56%,20%), /*down left*/
			-3px 0px 1px hsl(120,56%,40%); /*left*/
			text-decoration:none!important;
	}
	td.td4:nth-child(2) > a:nth-child(1):hover {
		text-shadow:
			-3px -3px 1px hsl(120,56%,20%), /*up left*/
			0px -3px 1px hsl(120,56%,20%), /*up*/
			3px -3px 1px hsl(120,56%,20%), /*up right*/
			3px 0px 1px hsl(120,56%,40%), /*right*/
			3px 3px 1px hsl(120,56%,40%), /*down right*/
			0px 3px 1px hsl(120,56%,40%), /*down*/
			-3px 3px 1px hsl(120,56%,40%), /*down left*/
			-3px 0px 1px hsl(120,56%,20%); /*left*/
	}

	.footer a,
	.tdc1 a,
	a.remove,
	.footer3 a,
	.footer9 a,
	a[style="color:#5f91c4"],
	.input2,
        #inner_container a,
        .item a,
        .topcontent a,
        a.link3,
        [style="color:#1E5EA9"],
        a[style="color: #1E5E95;font-size: 16px;display: inline"],
        td[style="color: #1C6593"],
        a[style="color: #1E5E95;font-size: 13px;display: inline; font-weight: bold"] {
		color: hsl(150,35%,45%)!important;
	}
        #addremove2 a, #addremove1 a {color:white!important;}

	a.showName,
	a.episodeinfo,
	p.legend,
	.footer3,
	.footer,
	.nextEpInfo {
		color: hsl(150,52%,20%)!important;
	}

	*[style="color: #5F91C4"] {
		color:hsl(150,37%,57%)!important;
	}

	.footer_real {
		background-color: hsl(150,35%,45%)!important;
		border-top: 1px solid hsl(157,54%,34%)!important;
	}

	tr[style="font-weight: bold; background-color: #5f91c4; color: #ffffff;"] * {
		background:hsl(150,35%,45%)!important;
		color:#fff!important;
	}

html > div {display:none!important;}

	table#settings td.leftCol,
	table#settings td.leftCol ul li a,
	table#settings td.leftCol ul li a:hover,
	table#settings td.leftCol ul li a.active,
	table#settings td.leftCol h5 {
		background:white!important;
		font-weight:bold!important;
	}

	table#settings td.leftCol ul li {
		border-bottom:none!important;
	}

	table#settings td.leftCol h5 {
		font-size: 16px!important;
		color: hsl(150,35%,45%)!important;
	}

	table#settings td.leftCol ul li a:hover {
		text-decoration:underline!important;
	}
	
	table#settings td.leftCol ul li a.active {
		font-style:italic;
	}

	.searchlinks a {color: rgb(255, 131, 53)!important;}
	
	.rightCol div[style="float: right;"] {float:left!important;}

        div.item div div[style="float:right"] {float:left!important;padding-right:10px;}

        div#addremovestring {display:inline!important;}
        div#show_name {display:inline!important;}

	a[style="color:#ff8335"]::before, a[style="color:#ff8335"]::after {
		content: "\00A0\2022\00A0";
		color: orange;
	}

	a[style="color: #999999"]::before, a[style="color: #999999"]::after {
		content: "\00A0\2022\00A0";
		color: #999999;
	}


        #addremove2 a, .link2 {background-color:hsl(210,40%,45%)!important;}
        #addremove2 a:hover, .link2:hover {background-color:hsl(210,40%,30%)!important;}

        .headlinehref a {color:#000!important;font-weight:bold!important;}

        a#but1.greenButtonRound, a#but2.orangeButtonRound {color:#FFF!important;}

        .calendar_buttons a {background-color:rgb(28, 147, 101)!important;}
        .calendar_buttons a:hover {background-color:rgb(9, 109, 71)!important;}
        .highlighteddayboxes2 {border: 1px solid rgb(255, 131, 53)!important;}
        .dayboxes {border:1px solid rgb(230, 228, 228)!important}

}
