css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jsbeautifier.org") {

  /* *** Custom inverse theme ***

    class_list = $.unique($(".CodeMirror-lines span").map(function() $.makeArray(this.classList)).get().sort());
    console.dir(class_list);
  */

  .CodeMirror { background-color: black !important; color: #f2f2f2 !important; }

  .CodeMirror-scrollbar-filler { background-color: black !important; }
  .CodeMirror-gutters { background-color: #191919 !important; border-right-color: #ddd !important; }
  .CodeMirror-linenumber { color: #666 !important; }

  .CodeMirror div.CodeMirror-cursor { border-left-color: white !important; }
  .CodeMirror div.CodeMirror-secondarycursor { border-left-color: silver !important; }
  .CodeMirror.cm-keymap-fat-cursor div.CodeMirror-cursor { background-color: #85f085 !important; }
  
  .CodeMirror-selected { background-color: #828282 !important; }
  .CodeMirror-focused .CodeMirror-selected { background-color: #404048 !important; }

  .cm-searching { background-color: rgba(64, 64, 0, .4) !important; }

  .cm-s-default .cm-keyword { color: #f0f020 !important; font-weight: bold !important; }
  .cm-s-default .cm-operator,
  .cm-s-default .cm-bracket { color: #ff8080 !important; font-weight: bold !important; }
  .cm-s-default .cm-comment { color: #60ff60 !important; font-style: italic !important; }
  .cm-s-default .cm-variable,
  .cm-s-default .cm-variable-3,
  .cm-s-default .cm-property { color: #e0e0e0 !important; }
  .cm-s-default .cm-def,
  .cm-s-default .cm-variable-2  { color: #80ffff !important; }
  .cm-s-default .cm-number,
  .cm-s-default .cm-string,
  .cm-s-default .cm-string-2 { color: #8080ff !important; }

  .cm-s-default .cm-atom { color: #219 !important; }
  .cm-s-default .cm-meta { color: #555 !important; }
  .cm-s-default .cm-error { color: #f00 !important; }
  .cm-s-default .cm-qualifier { color: #555 !important; }
  .cm-s-default .cm-builtin { color: #30a !important; }
  .cm-s-default .cm-tag { color: #170 !important; }
  .cm-s-default .cm-attribute { color: #00c !important; }
  .cm-s-default .cm-header { color: blue !important; }
  .cm-s-default .cm-quote { color: #090 !important; }
  .cm-s-default .cm-hr { color: #999 !important; }
  .cm-s-default .cm-link { color: #00c !important; }

  .cm-negative { color: #d44 !important; }
  .cm-positive { color: #292 !important; }
  .cm-header, .cm-strong { font-weight: bold !important; }
  .cm-em { font-style: italic !important; }
  .cm-link { text-decoration: underline !important; }

  .cm-invalidchar { color: #f00 !important; }

  div.CodeMirror span.CodeMirror-matchingbracket {color: black !important; background-color: #0f0 !important; }
  div.CodeMirror span.CodeMirror-nonmatchingbracket { color: black !important; background-color: #f22 !important; }

}
