css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("strims.pl") {

Body {background:#f3f3f3!important;}
#template.fixed #content {padding-top: 93px!important;}
#top_bookmarks li a {background:#F3F3F3!important;}
.entry_inner {margin-bottom:16px!important;}
p {margin-top:16px!important;}
.bookmark_contents_top {background:none!important;}
#top_bookmarks li.selected a {background: #F3F3F3!important;border-color: #acacac!important;border-width: 1px 1px 0px;margin: -1px;z-index: 2;}
.border_blue {border-color: #acacac!important;}
#top {padding-top:20px!important; background:#f3f3f3!important;}
.content_vote {height:20px!important; padding-top:10px!important; padding-bottom:10px!important;}

.dislike:hover, .dislike.selected, .level_3>.content_voting>.content_vote_wrapper>.dislike:hover {background:#ffddcf!important;}
.like:hover, .like.selected, .level_3>.content_voting>.content_vote_wrapper>.like:hover {background:#e7edd3!important;}
.content {margin-bottom:35px!important;}
.like, .dislike {background:#f7f7f7!important;}

.content_header {margin-bottom:10px!important;}
.content_image img {width:110px!important;}
#contents_page ul.contents > li div.content {background-color: white; border: 0 solid; border-radius: 3px 3px 3px 3px;box-shadow: 0 3px 5px -3px #A8A8A8;margin-bottom: 22px;margin-top: 22px;padding: 20px 10px 20px 10px!important;}
#contents_page .bold {font-weight: normal !important;}
.content>.content_inner>.content_info_basic>li>.color_gray {font-size:10px!important;}
.content_title{color:#343434!important;}
.user_name {font-weight:bold!important;}
.text_wrapper {background:#fff;}
ul.content_comments>li {margin-bottom:15px!important;}
.content_comment_text>.markdown>p {line-height:19px!important;}
.content_comment_info {margin-top:8px!important}
.content_info_actions {margin-top:10px!important;}
.menu_buttons>li>a:hover {background:#e4e4e4!important;}
div.content_comment div.content_comment_voting a.content_comment_vote, .entry_vote {background:none!important; border: solid 1px #C8C8C8!important;}
.content_image img {border: 1px solid #E1E1E1!important;}
a.content_title:visited{color:#999999 !important}
.footer_links_box>ul {background:none!important;}
.strim_subscribe.selected {background:#c1e4bf!important;}
.button.mixed:hover, .strim_block:hover, button.button:hover {background:#e8e8e8!important;}
.content_right_box {margin-bottom:15px!important; border-bottom: 1px solid #e8e8e8!important; padding-bottom:8px!important;}
h3.border {margin-bottom:15px!important; padding-bottom:5px!important; font-weight:bold!important;}
td.action>span {font-size:12px!important;}
.button.mixed.open {border-bottom:1px solid #C8C8C8!important;}
.section_right_box_info {margin-bottom:25px!important;}
#content_toolbar_voting>.content_vote_wrapper>.content_vote {border-right:solid 1px #e7e6e6!important; border-left:solid 1px #e7e6e6!important; height:11px!important; padding-top:5px!important; padding-bottom:14px!important;}
#content_toolbar_voting {margin-top:0px!important;}
.content_voting_users img {max-width:15px!omportant;}
}
