css:
    body.new-header
    {
        background: none !important;
    }


    #global-nav
    {
        height:30px !important;
        background:none !important;
        background-color:#888 !important;        
    }

    .gn-link, .gn-link:link, .gn-link:visited
    {
        height: 30px !important;
        padding-top:6px !important;
    }
    
    #global-nav .toplink
    {
        height: 30px !important;
    }

    
    #global-nav .tools
    {
        height:30px !important;
    }

    #global-nav .tools form 
    {
        height: 25px !important;
        margin-top: 2px !important;
    }
    
    #global-nav .tools form .search-icon
    {
        background-position:0 -136px !important;
    }
    
    #global-nav .tools form .gn-search
    {
        height:25px !important;
    }
    
    #global-nav .tools form .gn-search:focus
    {
        border-color: white !important;
        box-shadow: 0 0 1px 1px #666 inset !important;
    }
    
    #global-nav .gn-account
    {
        top: 0 !important;
    }
    
    #global-nav .gn-account .account
    {
        padding-top:0px !important;
    }
    
    #global-nav .gn-account .account img
    {
        width:29px !important;
        height:29px !important;
        
    }
    
    #global-nav .panel
    {
        top:28px !important;
    }
    
    .micro-card
    {
        background-color:#fff !important;
        border-bottom : 1px solid #eee;
        min-height : 61px !important;
    }
    
    .micro-card .background-image
    {
        display:none !important;
    }
    
    .micro-card .micro-image
    {
        width: 60px !important;
        height: 60px !important;
        position:absolute !important;
        top:0px;
    }
    .micro-card .content-section-wrapper
    {
        float : right !important;
        min-height : 61px !important;
        width:100% !important;
        padding-left:70px !important;
    }
    
    .micro-card .action-date
    {
        display:inline-block !important;
        color : #999 !important;        
    }
    
    .micro-card .action-author
    {
        font-size : 12px !important;
        font-weight:normal !important;
        color : #333 !important;
    }    
    
    .micro-card .action-author a
    {
        font-size : 12px !important;
        font-weight:normal !important;
        color : #0066CC !important;
    }
    
    .micro-card .content-section    
    {
        margin : 0px 10px 0px 70px !important;
    }

    .buddyiconWrapper
    {
        left : auto !important;
        right:0px !important;
    }

    .card-comment .commentsWrapper
    {
        width : auto !important;
    }
    
    .card-comment .comments-inner
    {
        padding : 0 0 0 75px !important;
    }
    
    .card-comment .comments-inner a
    {
        color: #0066CC !important;
    }

    .card-comment .comments-inner a:hover
    {
        color: #fff !important;
    }

    
    .comment-date a
    {
        color: #ccc !important;    
    }
    
    .subnav-refresh .subnav-middle
    {
        height: 73px !important;
    }
    
    .subnav-refresh ul.nav-links
    {
        margin-left:0px !important;
    }
    
    .cover-photo-holder
    {
        display:none;
    }
    
    .subnav-refresh .sn-avatar
    {
        display:none;
    }
    
    .subnav-refresh .person    
    {
        display:none;
    }
    
    .subnav-refresh .stats
    {
        bottom : 0 !important;
        right: 175px !important;
        z-index:999;
    }
    
    .card-comment .comments-inner p
    {
        color : #444 !important;
    }
    
    #comments small.comment-meta, #comments small.comment-meta a
    {
        color : #999 !important;
        font-size:11px !important;
    }
