css:
@-moz-document domain("southperry.net") {
 /* background color on navtabs */
.navdtabs ul.floatcontainer, .navbar {
background: #F4F4F4 !important;
}

 /* font color on navtabs */  
.navtabs li.selected a.navtab, .selected .floatcontainer a, .navbar_advanced_search li a {
color: #111 !important; 
}

 /* black down arrow */
.navtabs li.selected li a.popupctrl {
background: transparent url(http://i.imgur.com/kGnIczQ.png) no-repeat right center !important;
}

 /* [hover/active] black down arrow + colored blue */
.navtabs li.selected li:hover a.popupctrl, 
.navtabs li.selected li a.popupctrl.active {
background: #DDD url(http://i.imgur.com/kGnIczQ.png) no-repeat right center !important;
border: 1px solid #CCC !important;
}

/* B9C9E4 */

 /* border (plus background) on popups */
.selected .popupbody {
border: 0px solid #CCC !important;
background: #CCC !important;
}

 /* background and border inside the popup */
.selected .popupbody li {
border-top: 2px solid #CCC !important;
background: #CCC !important;
}

 /* backround on Selected */
.navtabs li.selected a.navtab {
    background: #F4F4F4  !important;
}
      
/* border underneath navbar */
.selected ul.floatcontainer  {border-bottom: 1px solid #DDD  !important;}  
    
/* Move Selected to far left */
.navtabs {padding-left:0px !important;}
  
/* close space between above_body and body_wrapper, but keep breadcrumbs in same spot */
.above_body {margin-bottom: 0 !important;}
.boddy_wrapper {padding-top:20px!important;}
.breadcrumb {margin-top:10px;}
  
 /* kill rounded corners on navbar */
.navtabs.floatcontainer, .navtabs ul, .navbar {
	-moz-border-radius-bottomleft: 0 !important;
	-moz-border-radius-bottomright: 0 !important;
	-webkit-border-bottom-left-radius: 0 !important;
	-webkit-border-bottom-right-radius: 0 !important;
	border-bottom-left-radius: 0 !important;
	border-bottom-right-radius: 0 !important;
}

 /* kill rounded corners on the top */
.body_wrapper { 
	-moz-border-radius-topleft: 0 !important;
	-moz-border-radius-topright: 0 !important;
	-webkit-border-top-left-radius: 0 !important;
	-webkit-border-top-right-radius: 0 !important;    
	border-top-left-radius: 0 !important;
	border-top-right-radius: 0 !important;
  }
}

@-moz-document domain("southperry.net") {
 /* make forumhead, etc have square tops */
.forumhead, .searchlisthead, .wgo_block, .blockhead, #popular_threads, .threadlisthead.table {
	-moz-border-radius-topleft: 0 !important;
	-moz-border-radius-topright: 0 !important;
	-webkit-border-top-left-radius: 0 !important;
	-webkit-border-top-right-radius: 0 !important;
	border-top-left-radius: 1px !important;
	border-top-right-radius: 1px !important;
}
  
 /* get rid of backgrounds/box shadows on forum list */
    
.forumrow.table {
    background:none!important;
      }
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead, .wgo_block, #popular_threads, .searchlisthead, .threadbit {
    -moz-box-shadow: 0 0 0 !important;
    -webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
      }

  
/* increase font size on the navlinks - inadvertently increases popup text size */
li.selected ul.floatcontainer a {font-size:105% !important;}
    
  
.blockhead {
  border: 0 !important;
    }
  
.navtabs li a.navtab:hover, .navgreen:hover, .navpurple:hover, .navyellow:hover, .navred:hover {
text-decdoration:underline !important;
border-top: 1px solid #CCC !important;
      }

.navtabs li a.navtab, .navgreen, .navpurple, .navyellow, .navred {
  border-top: 0px solid transparent !important;
    }

.navtabs .selected .navtab:hover{
  text-dedcoration:underline!important;
  border-top: 0px solid transparent !important;
}

.navbar { 
   bporder-left: 1px solid #AAA !important; 
  }

.navbar .selected { 
  border-top: 1px solid #CCC !important;
      }

/* selected background f4f4f4 */
a.navtab:hover, a.navpurple:hover, a.navgreen:hover, a.navyellow:hover, a.navred:hover {background: #f4f4f4 !important;}
}
