css:
@-moz-document domain("southperry.net") {
/* == SEARCH BOX ==
Moves searchbox over, making room for more buttons - I made an error in a previous CSS update:
delete: .textboxcontainer{background:something !important}
delete: .navbar_search{width: 180px;}*/
.navbar_search, .globalsearch {
    width: 132px !important;
      }

.globalsearch .textboxcontainer span {
  background: #04C url(http://www.southperry.net/images/gradients/forumhead.png) repeat-x left top !important;
}
/* == END SEARCH BOX == */

/* == POPULAR THREADS ==
Fixes popular_threads background overlap, adds box shadow, clears the buttons on search pages, better border coloring */

#popular_threads.blockbody.formcontrols.floatcontainer {
  -moz-border-radius-topright: 5px;
  -moz-border-radius-topleft: 5px;
  -webkit-border-top-right-radius: 5px;
  -webkit-border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  border-top-left-radius: 5px;
  -moz-box-shadow: -2px 2px 2px #B9C9E4;
  -webkit-box-shadow: -2px 2px 2px #B9C9E4;
  box-shadow: -2px 2px 2px #B9C9E4;
      }
#popular_threads tr, #popular_threads table, #popular_threads.formcontrols {
  border-color: #CECECE !important;
      }

#popular_threads.formcontrols{border:0 !important;}
  
#popular_threads {
clear:both !important;    
}
      
#popular_threads td {
  padding: 3px;
      }
/* == END POPULAR THREADS == */

/* == FIX POST BUTTON BACKGROUNDS == */
.textcontrols.floatcontainer a {
background-color: transparent !important;
}

a.banthread {
padding-bottom: 1px !important;
}
/* == END POST BUTTON BACKGROUNDS == */
  
/* == FIX HOVER COLORING ON colornav TABS == */
.navpurple:hover,.navyellow:hover,.navgreen:hover,.navred:hover {
  color:black !important;
}
  
/* == UPDATE navyellow ON GMS STATUS == */
  .navyellow {background: url(http://i.imgur.com/tLcY7lO.png) repeat-x left top !important;}
.navyellow:hover {background: #48D url(http://www.southperry.net/images/gradients/selected-tab-gradient-with-top-alpha.png) repeat-x scroll 0 -4px !important;}

/* == Fix pagination on VM walls == */
.vm_above_postlist {
  width: 218px !important;
  }

/* == Borders on infraction window(s) == */
.infraction_infotable td, .infraction_infotable th {
  border: 1px solid;
  text-align: left !important;
    padding: 5px !important;
  }
}
