css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {
#global-nav {height:30px !important;}
#global-nav .nav { height: 30px !important;}
#global-nav .nav > ul { height: 30px !important;}
#global-nav .toplink { height: 30px !important;}
.gn-link, .gn-link:link, .gn-link:visited { height: 30px !important; }
#global-nav .tools { height: 30px !important;}
#global-nav .tools form {  height: 24px !important;  margin-top: 3px !important; }
#global-nav .tools form .gn-search {height: 24px !important;}
#global-nav .tools form .search-icon {  background-position: 0 -138px !important;  height: 24px !important;  padding: 5px 5px 5px 0 !important;}
#global-nav .gn-account {  height: 33px;  top: 0 !important;}
.nav .gn-link {  padding-top: 7px !important;}
#global-nav .panel {top: 22px !important;}
#global-nav #gb-ac-wrap {top:20px !important; z-index:10000;}
}
