css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.reddit.com/r/PS4"), url-prefix("http://www.reddit.com/r/ps4") {

body { min-width: 1066px !important; padding-top: 80px !important; }

#header {
  background: rgba(10,10,10,.98) !important;
  position: fixed !important;
  top: 0 !important;
  left: 0 !important;
  right: 0 !important;
}
#sr-header-area {
  background: 0 !important;
  border: 0 !important;
}
#sr-more-link {
  background: #999999 !important;
  border-bottom: 1px solid #aaaaaa !important;
}
#header-bottom-left {
  background: url("http://i.imgur.com/6S0TODO.jpg") 0 -18px no-repeat !important;
  height: 80px !important;
}
#header-img-a {
  background: url("http://i.imgur.com/MNyfNur.png") 0 0 no-repeat !important;
}
.pagename a {
  background: url("http://i.imgur.com/MNyfNur.png") -40px 0 no-repeat !important;
  width: 90px !important;
  margin-left: 20px !important;
}
#header-bottom-left .tabmenu {
  box-shadow: none !important;
  top: 28px !important;
  left: 291px !important;
  clear: both;
  font-family: Arial, Helvetica, sans-serif !important;
  font-weight: bold !important;
  text-transform: uppercase;
}
#header-bottom-left .tabmenu li {
  border: 0 !important;
  margin: 0 15px 4px 15px !important;
}
#header-bottom-left .tabmenu li:last-child {
  box-shadow: none !important;
}
#header-bottom-left .tabmenu li a {
  background: 0 !important;
  font-size: 14px !important;
  line-height: 30px !important;
  padding: 0 !important;
  color: #999999 !important;
}
#header-bottom-left .tabmenu li a:hover {
  color: #ffffff !important;
}
#header-bottom-left .tabmenu li.selected {
  border-bottom: 4px solid #0661b2 !important;
  margin-bottom: 0 !important;
}
#header-bottom-left .tabmenu li.selected a {
  color: #ffffff !important;
}
#header-bottom-left .tabmenu li.selected a:hover {
  border: 0 !important;
}

.titlebox .usertext-body .md a[href="http://webchat.quakenet.org/?channels=#r/playstationchat"] {
  background: 0 !important;
  box-shadow: none !important;
  border: 0 !important;
  color: #ff6600 !important;
  position: fixed !important;
  top: 38px !important;
  left: 233px !important;
  font-size: 14px !important;
  text-transform: uppercase;
}

.titlebox .usertext-body .md ol {
  box-shadow: none !important;
  border: 1px solid #ffd955 !important;
  background: #ffebad !important;
  border-radius: 0 !important;
}
.titlebox .usertext-body .md ol strong { font-size: 11px !important; }


}
