css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("strims.pl") {
.content {margin-bottom:10px!important; padding-bottom:10px!important; border-bottom:1px solid #ccc!important}
.content_image img {min-width:110px!important;}
.content_image {float:right!important;}
.content_inner {float:left!important; width:820px!important; position:relative; top:-5px; margin-left:10px!important;}
#top,#top_bookmarks li a {background:#fff!important;}
.content_title {font-size:17px!important;}
.content_title:visited {color:#759dc1!important;}
.embed .image img{max-width:100%!important;}
.content_info_actions {max-width:700px; margin-top:6px!important;}
.content_info_url {margin-top:10px!important; margin-bottom:5px!important;}
.content_info_url a {font-size:10.5px!important; opacity:0.9;}
.content_info_basic {height:20px!important;}
.content_info_basic, .content_info_basic span {font-weight:bold!important;}
.content_info_basic a {background:#e0f4fd; padding:3px 4px 3px 4px!important;}
.content_info_basic .user_name {background:none;}
#top_bar_wrapper {background:#0f5e7f!important;}
#top {border-bottom:solid 1px #0f5e7f!important;}
#top_bookmarks li a {border-top: solid 1px #0f5e7f!important;border-left: solid 1px #0f5e7f!important; border-right: solid 1px #0f5e7f!important;}
#top_bookmarks li.selected { border-bottom:solid 1px #fff!important; position:relative; top:1px!important; background:red!important;}
.contents_right_box h3 {font-weight:bold!important;margin-top:30px!important;padding-bottom:5px!important; margin-bottom:15px!important;}
.content_right_box {margin-bottom:7px!important; padding-bottom:7px!important;  border-bottom:1px solid #e3e3e3!important}
#top_bar_wrapper .selected, #top_bar_more {background:#173e60!important;}
.content_note {background:#fefeef!important;}
.content_comment_text p {line-height:19px!important; margin-bottom:18px!important;}
.section_right_box_info {margin-top:15px!important; padding-bottom:15px!important;  border-bottom:1px solid #e3e3e3!important;}
.section_right_box_actions {margin-bottom:20px!important;}
.footer_links_box ul, #footer_links {background:#fff!important;}
.content_voting_users img {width:15px!important;}
}
