css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theoldreader.com") {
/* colors: 
(#30a67c > #4e7fff) 
(#257e5e > #426fd6)
(#bbdfd2 > #ccd4ed)
(#d9edf7 > #e6ecfa)
(#3a87ad > #5376c6)
(#bce8f1 > #cfddff)

(#da3f2d > #d57f45)

(#f9deda > #f5e2d6)
 */


a {color:#4e7fff;}
a:hover {color:#4e7fff;}
.btn-primary {background-image:linear-gradient(tobottom,#4e7fff,#426fd6)!important;}
.btn-link:hover {color:#4e7fff!important;}
nav .dropdown-toggle:hover .caret {border-top-color:#4e7fff !important;
    border-bottom-color:#4e7fff!important;}
.nav-list li.active a:not(.i):before {border-left-color:#4e7fff!important;}
.nav-list .droptarget {border-top:1px dotted #4e7fff!important;}
.post.unread {border-left-color:#4e7fff!important;}
.navbar .nav li a:hover,.landing .welcome a {color:#4e7fff!important;}
.label.label-feed,.badge.badge-info {background-color:#4e7fff!important;}
.nav-list li.active a,.nav-list li.active a:hover,.nav-list li a:hover {color:#4e7fff!important;}

.nano>.pane>.slider {background:#ccd4ed!important;}

a.social:hover {color:#d57f45!important;}
.nav-list.following-list .droptarget {
border-top:1px dotted #d57f45!important;}
.label.label-social,.nav-list li.social .badge {
background-color:#d57f45!important;}
.control-group.error input,.control-group.error select,.control-group.error textarea,.control-group.error input:focus,.control-group.error select:focus,.control-group.error textarea:focus,.nav-list li.social.active a:before {
border-color:#d57f45!important;}
.home-page .ourstuff ul:first-child li:first-child a,.nav-list li.social a:hover,.nav-list li.social.active a,.post blockquote small a,.post .btns .likes a {
color:#d57f45!important;}


.alert-info {
background-color:#e6ecfa!important;
color:#5376c6!important;
border-color:#cfddff!important;}
.alert.alert-error {
background-color:#f5e2d6!important;}
.control-group.info .input-prepend .add-on,.control-group.info .input-append .add-on {
color:#5376c6!important;
background-color:#e6ecfa!important;
border-color:#5376c6!important;}
.table tbody tr.info td {
background-color:#e6ecfa!important;}
.label-info,.badge-info {
background-color:#5376c6!important;}
.text-info,.control-group.info .control-label,.control-group.info .help-block,.control-group.info .help-inline,.control-group.info .checkbox,.control-group.info .radio,.control-group.info input,.control-group.info select,.control-group.info textarea,.alert-info h4 {
color:#5376c6!important;}
.control-group.info input,.control-group.info select,.control-group.info textarea {
border-color:#5376c6!important;}




.content-body a, a[href*="http://theoldreader.com/feeds/subscribe?url=%s"] {color:#4e7fff!important;}

.label:not(.label-transparent) {color:#fff !important;}
.label.label-feed {background-color: #4e7fff !important;}



/* Images */
.btn img[src*="http://s.yeoldereader.com/assets/ico-plus-726b785b5282f2658393e40f43d8a799.png"] {
    width:0!important;
    height:18px!important;
    padding-right:18px!important;
    background:transparent url("data:image/png;base64,") top left no-repeat !important;
}
.subscribe .icon {
    background-image: url("data:image/png;base64,") !important;
}
.form-search .btn {
    background-image: url("data:image/png;base64,") !important;
}
.navbar .brand {
    background-image: url("http://i1300.photobucket.com/albums/ag82/Renaultt/logo-a1_zps763e0abc.png") !important; /* backups: "http://cdn.imghack.se/images/421c7dfcb9bddee292de2f4e76f3c6ed.png" or "http://pastebin.com/CENqxEM9" (base64) */
}
.landing .logo a {
background-image: url("http://i1300.photobucket.com/albums/ag82/Renaultt/logo-landing-a1_zps05366217.png") !important; /* backups: "http://cdn.imghack.se/images/fc1e7e4ed8317c53aadb427411869fa8.png" or "http://pastebin.com/dQeJWWTT" (base64) */
}
.landing .sign-in ul li a.theoldreader {
background-image: url("data:image/png;base64,") !important; }

.loader {
background-color: #fafafa !important;
background-image: none !important; /* no pacman */
}

/*[[changelvl]]*/

}

style_setting_options.value:
/*====================== Other Changes ======================*/

body {color:#333!important;}
body, .reader .floating {background-color:#f5f5f5!important;}

/* Buttons */
.btn {color:#4a4a4a!important;}
.btn-primary:not(.btn-large) {color:#fff!important;}

/* Feed Folders Title */
.nav-list li.nav-header a {color:#c27440!important; font-size:12px!important;}
.nav-header.dropdown .badge {background-color:#c27440!important;}

/* Feed Header URL */
.clr span.label.label-inverse {background-color:#d8d8d8!important; text-shadow:none!important;}
.clr span.label.label-inverse a {color:#777!important;}
