css:
@-moz-document domain("ruby-doc.org") {
.adzbox,
.adzbox-index,
#carbonads-container,
.carbonad,
#azcarbon {
   display: none !important;
}

code {
   padding: 2px !important;
   font-family: monospace !important;
   background-color: #ececec !important;
}

.wrapper {
   width: 100% !important;
}

.class #metadata,
.file #metadata,
.module #metadata {
   font-family: arial, sans-serif !important;
   font-size: 12px !important;
}

ul.link-list {
  margin: 2px 2px 2px 4px !important;
  padding: 4px !important;
}
ul.link-list li {
   margin-top: 6px !important;
   margin-bottom: 6px !important;
   line-height: 14px !important;
}

ul.link-list .type {
  padding: 0px 4px !important;
}

.class  div#documentation,
.file   div#documentation,
.module div#documentation {
   max-width: 95% !important;
   margin: 1em 1em 5em 16em !important;
}

.documentation-section {
  margin: 2px 2px 4px 4px !important;
}

#metadata .section { font-size: 100% !important; }

.section {
   margin: 0 !important;
   padding: 0 !important;
   font-size: 11px !important;
   background-color: #d4dde5 !important;
}

span.type { color: #4b779f !important; }

#documentation .section
#constants-list {
   padding: 0 !important;

}

.method-section,
#constants-list {
   font-size: 100% !important;
}

#documentation h3.section-header {
   margin: 0 0 1em 0 !important;
   padding: 4px .5em !important;
}

#constants-list > dl {
   margin: .5em .5em !important;
}

div.method-heading {
   margin-left: 4px !important;
   font-size: 20px !important;
   line-height: 24px !important;
   background-position: 0% 50% !important;
}

div.method-detail p {
   margin: .75em 2em .75em .5em !important;
}

pre {
   margin: .5em auto !important;
   padding: .5em !important;
   width: 95% !important;
   border: 1px solid;
   border-color: #e1e2e3 #ccc #ccc #e1e2e3 !important;
}

p code {
   background-color: #e9edf0 !important;
}

pre, code {
   font-size: 14px !important;
}
}
