css:
@-moz-document domain("u-skateparade.nl") {
  #foto {
    background: #119efc; /* Old browsers */
    background: -moz-linear-gradient(top,  #119efc 0%, #a1dbff 100%, #c5e7ff 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#119efc), color-stop(100%,#a1dbff), color-stop(100%,#c5e7ff)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #119efc 0%,#a1dbff 100%,#c5e7ff 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #119efc 0%,#a1dbff 100%,#c5e7ff 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #119efc 0%,#a1dbff 100%,#c5e7ff 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #119efc 0%,#a1dbff 100%,#c5e7ff 100%); /* W3C */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#119efc', endColorstr='#c5e7ff',GradientType=0 ); /* IE6-9 */
  }
  #fotorol {
    display: none;
  }
}
