css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.hk") {
html, body {background-color: #FFF !important;font-family:"Trebuchet MS", Trebuchet, sans-serif !important; font-size: 16px !important;}
form {font-size: inherit !important;}
body {padding: 10px 20px !important;}
.reply {background-color: #ECF3F7 !important; padding: 5px !important;}
.post .reply { border: none !important;}
.oppost {background-color: #E1EBF2 !important; border-radius: 4px; overflow: auto; padding: 5px;}
a {color: #105289 !important;}
a:hover {color: #D31141 !important;}
blockquote a:hover {background-color: #D0E4F6 !important;}
.postername {color: #AA0000 !important; font-weight: bold !important;}
#postform {background-color: #ECF1F3 !important; margin: 10px 0 !important; }
.theader {border: none !important; color: #115098 !important;}
#postform td.label label, #postform td.label {color: #425067 !important;}
hr {border-color: #CCCCCC #FFFFFF #FFFFFF !important;}
.rmenu, #adminbar {background-color: #12A3EB !important; width: 99% !important; padding: 10px !important; color: #EEEEEE !important;}
#title {text-align: left !important;}
.rmenu a, #adminbar a {color: white !important;}
#adminbar {border-radius: 4px 4px 0 0;} .rmenu {border-radius: 0 0 4px 4px;}  #boardNavBottom {border-radius: 4px !important;}
#adminbar + * + HR {display: none;}
.logo {margin-top: 10px !important; font-weight: normal !important;}
.rules {font-size: 12px !important;}
.reflink a {color: #333 !important;}
}
