css:
@-moz-document domain("mail.google.com") {
a[title^="Chats"]:after {
    content: url("https://ssl.gstatic.com/ui/v1/icons/mail/wbt/hangouts_icon16.png");
    padding-left: 2px;
    position: relative;
    top: 1px;
}
a[title^="Starred"]:after {
    content: url("https://ssl.gstatic.com/ui/v1/icons/mail/superstars/star_lit_red3.png");
    padding-left: 1px;
    position: relative;
    top: 1px;
}
a[title^="--Starred2"]:after {
    content: url("https://ssl.gstatic.com/ui/v1/star/star-lit4.png");
    padding-left: 1px;
    position: relative;
    top: 1px;
}
a[title^="Spam"]:after {
    content: url(data:image/gif;base64,);
    padding-left: 2px;
    position: relative;
    top: 1px;
}
a[title^="Inbox"]:after {
    content: url("http://www.cancerletter.com/images/mail_icon.gif");
    padding-left: 3px;
    position: relative;
    top: 3px;
}
a[title^="Drafts"]:after {
    content: url("https://upload.wikimedia.org/wikipedia/commons/6/6f/Black_Pencil_Icon.png");
    padding-left: 3px;
    position: relative;
    top: 1px;
}
a[title^="Sent"]:after {
    content: url("http://resources.esri.com/help/9.3/arcgismobile/adf/dotnetimages/PublicMethod.gif");
    padding-left: 3px;
    position: relative;
    top: 1px;
}
a[title^="--Groupon"]:before {
    content: url("http://www.heinemann.com/shared/index_files/cart.png");
    position: relative;
    top: 1px;
}
a[title^="--BACN"]:before {
    content: url("http://www.ci.commerce.ca.us/common/images/CivicAlerts/item_arch.gif");
    position: relative;
    top: 1px;
}
a[title^="--Waiting"] {
    position: relative;
    left: -4px;
}
a[title^="--Waiting"]:before {
    content: url("https://ssl.gstatic.com/ui/v1/icons/mail/superstars/star_lit_purple_question3.png");
    position: relative;
    top: 1px;
    left: 3px;
}
}
