css:
@-moz-document url-prefix("http://eu.battle.net/wow/ru/forum/"), url-prefix("http://eu.battle.net/wow/en/forum/") {
#content .content-top {
background: none;
background: #ECD4A2;
}

.post-detail blockquote.quote-public {
color: #8D2F12 !important;
}

.post, .post .deleted {
background-image: none;
background-color: #FFF0D7;
border-bottom: 1px dotted #E2C18F;
}
.post-detail {
    color:#000;
  }

.color-q1, .color-q1 a {
color: #FF8A2D !important;
}
  
#forum-content .section-header {
color: #7E4C2B;
font: 18px Arial;
text-transform: none;
}

#postCountdown {
  color:#000;
}
  
a {
color: #5F1F0E;
}
  
  .post-user, .talkback .post-user {
      background-image: none;
      
    }
    
.post-user .character-info {
top: 24px;
width: 120px;
background-color: #A3774D;
padding: 5px;
}
    
    .post-info .postData, .post-info .postData a {
color: #E96120;
}
    
    .karma-feedback, .prev-vote {
background: none;
border: none;
}
    

    
    .wow-class-5, .wow-class-5 a {
color: #FFF !important;
}
    
.button1 span {
background-image: none;
background: #AD8062;
background-repeat: no-repeat;
color: #FFF9EC;
}

.post-user, .talkback .post-user {
background-image: none;
width: 170px;
}

    
    .wow-class-3, .wow-class-3 a {
color: #6BB408 !important;
}
    
    .post-user .avatar-interior img, .post-user .avatar-interior {
height: 84px;
width: 84px;
}

.post-user .avatar {
left: 22px;
top: 15px;
}

#posts tr:hover td {
background: #ECBD6A;
}

#posts td {
border-bottom: 1px dotted #D6AA68;
}

.forum-options a {
color: #302B20;
}

#posts .post-author, #posts .post-lastPost a {
color: #9B4414;
}

#content .content-bot {
background: none;
min-height: 600px;
}

#posts tr.read .post-title a {
color: #C53916;
}

#posts .post-pages .ui-pagination li a {
color: #C5432A;
}

#forum-content .forum-search input {
padding-right: 30px;
background: #FDEBB0;
}

#forum-content .forum-search input {
width: 200px;
padding: 5px;
color: #6D4532;
}

.ui-pagination li.current a {
background-color: #AD7C4E;
color: #FFF;
font-weight: bold;
}

.ui-pagination li a:hover {
background-color: #C07026;
color: #FFF;
}

.ui-pagination li a {
background: #9C6139;
color: #EBCFA0;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
border-radius: 3px;
}

.talkback, #report-post {
background-image: none;
background-color: #DFC391;
}

.button2 span {
background-image:none;
background: #C5854A;
background-repeat: no-repeat;
color: #FFF;
}

#thread .blizzard {
background-image: none;
background-color: #FFF0D7;
background-repeat: no-repeat;
border-bottom: 1px solid #D5C4BB;
}
.blizzard .post-detail {
color: #1152B3;
}
.blizzard .post-detail a{
color: #1152B3;
}
.blizzard .post-detail blockquote {
border-color: #002555;
color: #FCFEFF;
}

.post-info .postData .date, .post-edited {
color: #C0A999;
}

.wow-class-10, .wow-class-10 a {
color: #1AB188 !important;
}

.wow-class-9, .wow-class-9 a {
color: #D9CDFF !important;
}

#posts .post-status {
display: block;
float: left;
padding-right: 7px;
color: #000;
font-size: 11px;
}

#posts .type-blizzard {
color: #154A8D;
font-weight: bold;
white-space: nowrap;
}

.ui-context {
display: none;
overflow: hidden;
position: absolute;
left: 0;
top: 15px;
z-index: 9000;
opacity: .98;
width: 225px;
background: #DBB58C;
border: 1px solid #262626;
font: normal 12px "Arial", sans-serif;
border-radius: 5px;
box-shadow: #3F3325 0 0 10px;
}
  
.ui-context .context .context-links a:hover {
background-color: #DA9041;
}
    

  
.ui-context a {
color: #FFF;
text-decoration: none;
}
  
.post-info .postData .date, .post-edited {
color: #E4803B;
}

.wow-class-8, .wow-class-8 a {
color: #25B2E4 !important;
}

.wow-class-11, .wow-class-11 a {
color: #E0812D !important;
}

.wow-class-2, .wow-class-2 a {
color: #EE8FB9 !important;
}

.wow-class-3, .wow-class-3 a {
color: #ADEB5B !important;
}

.wow-class-4, .wow-class-4 a {
color: #FCFC50 !important;
}

.wow-class-5, .wow-class-5 a {
color: #FFF!important;
}

.wow-class-7, .wow-class-7 a {
color: #3351AF !important;
}

.wow-class-9, .wow-class-9 a {
color: #CFC0FF !important;
}

.blizzard .post-detail b, .blizzard .post-detail strong {
color: #1D3C8D;
}

.wow-class-1, .wow-class-1 a {
color: #A76117 !important;
}

.mvp .post-detail {
color: #49940E;
}

.post-user .character-info {
top: 14px;
width: 120px;
}


.blizzard .post-detail blockquote {
border-color: #002555;
color: #1F2A30;
}

.context-link {
padding-right: 14px;
color: #387FCC;
}

.post-detail blockquote.quote-blizzard {
border-color: #002555 !important;
color: #1152B3 !important;
}

a:hover {
color: #BE3A3A !important;
}

.post-detail blockquote {
border: 1px solid #F1D4C1!important;
background: #FFF0DF;
border-bottom:none;
margin: 0 0 5px 25px;
padding: 10px;
border-radius: 5px;
}

.forum-link .desc {
color: #3F3B34;
}

.forum-link:hover {
background-color: rgba(167, 93, 36, 0.35);
}

.forum-link {
background-color: rgba(207, 162, 93, 0.25);
color: #7E5128;
}

#station-view .readmore {
font-size: 12px;
text-transform: uppercase;
color: #5F290F;
padding-top: 3px;
border-bottom-width: 2px;
}

#station-content .station-inner-wrapper {
padding: 10px 14px;
background: #ECD4A2;
}

.coc {
color: #71401F;
margin: 0;
text-transform: none;
font-size: 11px;
width: 290px;
background: #E6C489;
font-style: italic;
}

.trending-topics li {
border-bottom: 1px solid #E0BD87;
color: #71401F;
}

.station-home #content .content-top {
background: #ECD4A2;
}

#station-view .bt-mask-l, #station-view .bt-mask-r {
background-image: none;
background-position: 0 -58px;
}

#station-view .bt-set {
background-image: none;
}

#station-view .bt-set a {
width: 307px;
height: 193px;
display: block;
position: relative;
float: left;
background: #E4C286;
border-radius: 15px;
margin-right: 5px;
}

#station-view .readmore {
font-size: 12px;
text-transform: uppercase;
color: #000;
border-bottom-width: 2px;
background:none;
}

#station-view .bt-set a:hover {
background: #ECBC75;
border-radius: 15px;
}

#station-view .bt-set a .desc {
color: #1C3996;
}

.context-link {
padding-right: 14px;
color: #F7FBFF;
}

#header .user-plate .player-name, #header .user-plate .guild-name {
display: block;
text-shadow: 1px 1px 1px #110E0E;
color: #F0D7C4;
font-weight: bold;
font-size: 12px;
width: 250px;
overflow: hidden;
white-space: nowrap;
}

#forum-content input, #forum-content textarea {
background: #F1E2D1;
padding: 10px;
}

.context-link {
padding-right: 14px;
color: #105FAD;
}

#header .user-plate .character-name {
line-height: 100%;
font-weight: bold;
font-size: 24px;
margin: 0;
padding: 0;
color: #FFF;
background: none;
letter-spacing: -0.0em;
text-shadow: 1px 1px 1px #000;
letter-spacing: -0.05em;
}

.post-detail code, .post-detail pre {
border-color: #AF8B73;
background: #CFAF90;
color: #110F0E;
}

.post-user .achievements {
float: left;
padding-left: 0;
padding-right: 11px;
color: #FFF;
}

.ui-breadcrumb li.last a {
color: #88523D;
font-weight: bold;
}

.post .mvp-title {
color: #C9FCA1;
}

.wow-class-11, .wow-class-11 a {
color: #FFB471 !important;
}

.wow-class-7, .wow-class-7 a {
color: #1F4DDD !important;
}


#station-view .bt-link.readmore {
margin-top: 23px;
}

.wow-class-8, .wow-class-8 a {
color: #86DFFF !important;
}

.post-user .character-info a {
      color:#fff;
    }

.wow-class-6, .wow-class-6 a {
color: #FF5976 !important;
  }
  
  .wow-class-10, .wow-class-10 a {
color: #5DFACF !important;
}
  
  .wow-class-1, .wow-class-1 a {
color: #F0D5BF !important;
}

.wow-class-11, .wow-class-11 a {
color: #FF9C44 !important;
}

.post-user, .talkback .post-user {
height: 133px;
padding-right: 80px;
}

.talkback-code {
background-color: #CAA061;
  background-image:none;
color: #D3AC6B;
padding-left: 30px;
}

.talkback-icon {
padding-left: 70px;
color: #63251A;
}

.talkback-code strong {
color: #FFEEAB;
}

.talkback-code-interior {
background-image:none;
padding: 30px;
padding-left: 15px;
}

#forum-content a:hover {
text-decoration: none;
color: #E21111!important;
}
}
