css:
.b-footer-back {
	width:0px;
	height:0px;
	float:left;
	border-right:1px solid #94989f;
	position:relative;
	cursor:pointer;
}

.b-footer-back-icon {
	width:0px;
	height:0px;
	top:50%;
	left:50%;
	margin:-15px 0 0 -16px;
	position:absolute;
	display:block;
	background:url(http://schedule.arbitr.ru/Content/Schedule/img/back.png) no-repeat;
}

.b-header_logout{
	position:absolute;
	right:0;
	top:0;
	width:0px;
	height:0px;
	line-height:69px;
	font-size:70%;
	text-align:center;
	border-left:1px solid #383c45;
}
