css:
@-moz-document domain('joindiaspora.com') {/* DOMAIN START */

body {
  font-family: "Titillium Web", sans-serif;
  padding: 40px 0 0 0;
  margin: 0;
}
body div.container,
body div.container div.span-24.last {
  width: 100%;
}
body input,
body textarea {
  font-family: "Titillium Web", sans-serif !important;
}
body > header {
  background: #222;
  padding: 0;
  height: 40px;
  box-shadow: 0 2px 25px rgba(0, 0, 0, 0.8);
}
body > header #global_search form input {
  border-radius: 4px;
}
body > header .dark-header .diaspora_header_logo {
  background: url(http://diaspora-site.vispress.com/assets/diaspora-logo-footer.png);
  background-position: 0px 2px;
  width: 150px;
  background-repeat: no-repeat;
}
body > header .dark-header {
  height: 30px;
  padding: 5px;
  box-shadow: inset 0px 0px 20px #000000;
}
body > header .dark-header #user_menu {
  margin-right: 10px;
}
body > header .dark-header #user_menu .user-menu-more-indicator {
  font-size: 6pt;
  padding: 0 5px;
  line-height: 20px;
}
body > header .dark-header .container {
  width: 100%;
}
body > header .dark-header .container > a {
  width: 15%;
  float: left;
}
body > header .header-nav > span {
  border: none;
  font-weight: normal;
}
div#leftNavBar {
  margin-right: 0;
  border-right: 1px solid #ccc;
  position: fixed;
  left: 0;
  background: #eee;
  height: 100%;
  top: 0;
  padding-top: 40px;
  width: 15%;
}
div#leftNavBar a {
  font-weight: normal;
}
div#leftNavBar #stream_selection > li {
  padding: 0px;
  margin: 0px;
}
div#leftNavBar #stream_selection > li a {
  display: block;
  padding: 10px 20px;
  margin: 0;
}
div#leftNavBar #stream_selection > li a:hover {
  color: white;
  background: #0097ff;
}
div#leftNavBar #home_user_badge {
  background: white;
  margin: 20px;
  padding: 10px;
  border-radius: 4px;
  box-shadow: 0 0 5px #aaa;
}
.rightBar.last {
  background: #eee;
  width: 20%;
}
.rightBar.last .section {
  margin: 0;
  padding: 10px 20px;
}
.rightBar.last .section .title .icons-heart,
.rightBar.last .section .title .icons-plus,
.rightBar.last .section .title .icons-question,
.rightBar.last .section .title .icons-bookmark {
  display: none;
}
.rightBar.last .section .title.no_icon {
  padding: 0;
}
.rightBar.last .section .content {
  padding: 0;
  font-weight: 8pt;
}
.rightBar.last .section .stream-faces {
  padding-top: 10px;
}
.rightBar.last .section .stream-faces img {
  border-radius: 4px;
}
.rightBar.last .section h5.title-header,
.rightBar.last .section h5.stream_title {
  margin-left: 0;
  font-size: 10pt;
  font-weight: normal;
  color: black;
}
div.span-13.append-1 {
  margin-left: 15%;
  width: 65%;
  /* left: 15%; */
  margin-right: 0;
  padding-right: 0;
}
#aspect_stream_container #main_stream div.media a img {
  border-radius: 8px;
  /*transform: rotate(-12deg) translate(5px,5px);*/
}
.stream_container#aspect_stream_container {
  margin: 0;
  border: none;
  padding: 20px;
}
.stream_container#aspect_stream_container h3.stream_title {
  padding: 0;
}
.stream_container#aspect_stream_container #publisher {
  padding: 0;
}
.stream_container#aspect_stream_container #no_contacts {
  background: #eee;
  width: 60%;
  padding: 20px;
  border-left: 4px solid #aaa;
}
/* Not-so-flat buttons */
#publisher .options_and_submit .public_toggle .button.toggle.publisher {
  font-size: 8pt;
  width: auto;
}
#publisher .options_and_submit .public_toggle .button.toggle.publisher span.text {
  line-height: 20px;
}
#publisher .options_and_submit .public_toggle .button.toggle.publisher span.text,
#publisher .options_and_submit .public_toggle .button.toggle.publisher i {
  font-size: 10pt;
}
#publisher .options_and_submit .public_toggle button,
#publisher .options_and_submit .public_toggle input[type="button"],
#publisher .options_and_submit .public_toggle input.button.creation,
#publisher .options_and_submit .public_toggle .button.toggle.publisher {
  line-height: 30px;
  padding: 0 10px;
  display: inline-block;
  color: black;
  background-color: white;
  box-shadow: inset 0px -2px 15px rgba(0, 0, 0, 0.15);
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  border-bottom: 2px solid rgba(0, 0, 0, 0.3);
  border-top: 1px solid rgba(0, 0, 0, 0.2);
  border-left: 1px solid rgba(0, 0, 0, 0.2);
  border-right: 1px solid rgba(0, 0, 0, 0.2);
  transition: box-shadow 0.3s;
  border-radius: 3px;
}
#publisher .options_and_submit .public_toggle .nsf-btn:hover,
#publisher .options_and_submit .public_toggle .nsf-btn:focus {
  box-shadow: none;
  transition: box-shadow 0.3s;
  color: black;
}
#publisher .options_and_submit .public_toggle input.button.creation {
  background-color: #3f8fba;
  text-shadow: 0px 0px 1px black;
  color: white;
  background-image: none;
  font-size: 10pt;
}
#publisher .options_and_submit .public_toggle .nsf-btn.nsf-secondary {
  background-color: #5E792C;
  text-shadow: 0px 0px 1px black;
  color: white;
}
#publisher .options_and_submit .public_toggle .nsf-btn:hover.nsf-primary {
  color: white;
}
/* General overrides */
a:hover {
  text-decoration: none;
}
/* clearfix helpers */
.cf:before,
.cf:after {
  content: " ";
  display: table;
}
.cf:after {
  clear: both;
}
	
}/* END DOMAIN */
