css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com")
{
    /* flatten all the things */

    *
    {
        box-shadow: none ! important;
        border-radius: 0 ! important;
        text-shadow: none ! important;
    }
    
    body
    {
        background: #F6F6F6 ! important;
    }
    
    /* header bar */

    #header
    {
        background: #1C506E ! important;
    }
    
    /* header bar buttons */
    
    .header-btn:hover
    {
        color: #FFFFFF ! important; 
    }
    
    .header-notifications, .header-boards, .header-logo
    {
        background: none ! important;
    }
    
    .header-help
    {
        display: none ! important;
    }
    
    /* header bar user image */
    
    .header-auth
    {
        margin: 0 ! important;
        border: none ! important;
    }

    /* home page links on right hand side */
    
    .org-profile-logo
    {
        border: none ! important;
        background: none ! important;
    }
    
    .sidebar-nav a.active
    {
        background: #1B5A7E ! important;
    }
    
    .sidebar-nav a:hover
    {
        background: #2887BD ! important;
    }
    
    .profile-member
    {
        border: none ! important;
    }
    
    /* home page board list */
    
    .board-list li
    {
        border: none ! important;
    }
    
    .board-list li a:hover
    {
        background: #1B5A7E ! important;
    }
    
    .member
    {
        border: none ! important;
    }
    
    .board-widgets .member
    {
        margin-right: 4px  ! important;
    }
    
    .member-type, .status
    {
        border: none ! important;
    }
    
    .status
    {
        border: none ! important;
    }
    
    /* home page username */
    
    .member-detail-modal .window-sidebar div:nth-of-type(1)
    {
        display: none;
    }
    
    .member-detail-modal .window-sidebar hr:nth-of-type(1)
    {
        display: none !important;
    }
    
    /* board */
    
    #board
    {
        background: none ! important;
    }
    
    .list
    {
        background: #E0E0E0 ! important;
    }
    
    /* board header links */
    
    .dark-hover:hover
    {
        color: #000000 ! important;
        background: none ! important;
    }
    
    /* lists and cards on board */
    
    .list, .list-card
    {
        border: 0 ! important;
    }
    
    .list-gradient-top, .list-gradient-bottom
    {
        display: none ! important;
    }

    /* add a card link */
    
    .open-card-composer
    {
        font-weight: bold;
    }
    
    /* card add button */
    
    input.primary
    {
        background: #1B7E1E ! important;
    }
    
    input.primary:hover
    {
        background: #24A828 ! important;
    }
    
    /* board sidebar */
    
    .board-side-btn:hover
    {
        background: none ! important;
    }

    .arrow
    {
        border-color: transparent #E0E0E0 ! important;
    }
    
    /* links on right hand side of board */
    
    .button-link
    {
        border: none ! important;
        background: #FFFFFF ! important;
    }
    
    .button-link:hover
    {
        background: #1B5A7E ! important;
    }
    
    /* actions */
    
    .new-comment-input
    {
        border: none ! important;
    }
    
    .action-comment
    {
        border: none ! important;
    }
    
    .badge
    {
        border: none ! important;
    }
    
    .task-progress-total
    {
        border: none ! important;
    }
    
    .on
    {
        background: #1B7E1E ! important;
    }
    
    .phenom
    {
        border: none ! important;
    }
    
    /* hide advert */
    
    .js-bc-ad, .bc-pitch, .ipad-promo-sidebar
    {
        display: none ! important;
    }
}
