css:
@-moz-document url-prefix("http://t.qq.com/"), domain("t.qq.com") {
label[for="msgTxt"],  #talkBox h2 a,#homeBannerNew, #app_recStars,#app_subscriptions .tit>strong:nth-of-type(1),#app_subscriptions .tit span,#medal_box,#sideBanner,#homeBannerNew,#app_ad,#app_ad3_1,#app_actPromo2,#app_actPromo,#weibo_SJ_WDZY_F_banner,#app_followHisAlsoFollow,#app_hotTweet, .pv-bottom-ads, .SC_ad, .mobileBox , .sc_appEnter
    {display:none !important;}
	.main_bn , .w_qk_side, .medal_arr
	{display:none !important;}
	 .widgetWrap , .SC_tjpd_mod
	{display:none !important;}
	.talkBoxBg, .insertFun , .m_profile .avatar , .e_orginCnt_b .mFun_out_abg , .search_tab, .fl_note, .tabStyle2, .side, .u_info_hd, .selTagBox, .tagsList a
    {background:none !important;}
	.wbpmlist .wbpmunit, .top, .bot, .ldListBox li.last, .AL .title, .homeTit, .w_qk_side, .D .bg, .setFaceSuc, .temperTexFrm, .share_wb_pn, .blueFoot, .uc_c2, .close_page
    {background:none !important;}
	.sideNav li , .taskList , .co_sc , .throughArea, .temperCirFrm, .share_wb_pn {border:none !important;}

#mainWrapper {background-color: rgba(255,255,255,0.50) !important;}
.main, .talkBox {background-color: rgba(255,0,0,0);border-right-color:rgba(228, 228, 228, 0) !important;}
.mainWrapperBg, .mainCnt , .m_profile_a {background: rgba(255, 255, 255, 0.30) !important;}
.replyBox, .orderWrap, .zfWrap, .relayList, .talkWrap, .uc_in, .ui_tab_new, .inputTxt, .wbpmlist {background: rgba(248, 248, 248, 0.45) !important;}
.head_qk_wrap .w_qk_bd, .bl_1 .bl {background: rgba(255, 255, 255, 0.68) !important;}
.insertFun, .talkBoxBg,.HSR .cntBox, .HSR .top, .peopleWrap  {background: rgba(255, 255, 255, 0.0) !important;}
.ui_tab {background: -webkit-gradient(linear,0 0,0 100%,from(rgba(254, 254, 254, 0.27)),to(rgba(244, 244, 244, 0.81)));}
.taskList ul li {border: 1px solid rgba(255, 255, 255, 0.05) !important;}
.D .DWrap, .co_body , .col-sub .vApprove , .throughArea {background-color: rgba(255, 255, 255, 0.48)  !important;}
.SC{border-top-color: rgba(228, 228, 228, 0) !important;}
.LC li, #talkList li:hover {border-bottom: rgba(228, 228, 228, 0) !important;}
.topList li  {border-bottom: none !important;}
.D2 .DWrap {background: rgba(255, 255, 255, 0.57) !important;}
.manage_mrc {background-color: rgba(249, 249, 249, 0.55) !important;}
.side-background-color, .frameset001 .col-main {background-color: rgba(239, 248, 231, 0) !important;}
.co_main {background: rgba(255, 255, 255, 0.14) !important;}
.big, .vPlay,.insertFun .dropBox {background: rgba(255, 255, 255, 0.20) !important;}
.headShadow {background-color: rgba(43, 43, 43, 0.17);background-image: linear-gradient(to bottom,rgba(44, 44, 44, 0.29),rgba(25, 25, 25, 0.55)) !important ;}
 
.comWrap .title h3 , .topListBox h3 {background:none; border-left:none !important;}
.comWrap .title , .find_top_mess , .subTab1 , .topList , .tabStyle5 , .blueFoot {border-bottom:none ; border-top:none !important;}
.m_profile_a {margin-top: -55px !important;}
.m_profile_a {padding:0px !important;}
.m_profile_a .m_profile_img {height: 300px !important;}
.avatar {border-radius: 70px !important;}
.m_profile .avatar img {border-radius: 70px !important;}
.m_profile_img img {width: 100% !important;}
.m_profile_img .m_profile_bg {top: 250px  !important;}
.changeCover {top: 272px !important;}
.topic_talk {background: rgba(248, 248, 248, 0) !important;}
.dc_pickMsg .LC .content  ,.dc_fall .item, .topicFunBox {border: none !important;}
.AL .title, .homeTit {border-bottom: none !important}
.HSR .cntBox, .HSR .top, .HSR .bot {border-left: none;border-right: none; border-top: none !important;}

.userPic img {border: 1px solid #ccc;-webkit-border-radius: 12px;-moz-border-radius: 2px;border-radius: 25px;-webkit-box-shadow: 0px 2px 3px #070303;-moz-box-shadow: 0px 2px 3px #F2F2F2;box-shadow: 0px 2px 3px #F2F2F2;}
.m_profile_h .avatar img {border-radius: 25px;}
.head_qk_wrap .w_qk_app {width: 400px !important;}
}
